import React, { useState, useEffect, useMemo, useRef, useCallback } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, BarChart, Bar } from 'recharts';
import * as THREE from 'three';

const AdvancedWaferAnalysisSimulator = () => {
  // 시뮬레이션 파라미터
  const [params, setParams] = useState({
    R_wafer: 150,
    R_pad: 400,
    rpm_pad: 31,
    rpm_wafer: 30,
    R_c: 225,
    time_total: 150,
    num_time_steps: 500,
    oscillation_enabled: true,
    oscillation_amplitude: 40,
    oscillation_speed: 300/60,
    num_wafer_grid: 400,
    heatmap_grid_size: 20 // 히트맵용 격자 크기 (20x20 = 400개 점)
  });

  const [currentView, setCurrentView] = useState('trajectory');
  const [isSimulating, setIsSimulating] = useState(false);
  const [results, setResults] = useState(null);
  const [selectedPoints, setSelectedPoints] = useState([]);
  const [userSelectedPoints, setUserSelectedPoints] = useState([]);
  const [customPoints, setCustomPoints] = useState([]); // 사용자 정의 점들
  const [newPointRadius, setNewPointRadius] = useState(75);
  const [newPointAngle, setNewPointAngle] = useState(0);
  const [isSelectingMode, setIsSelectingMode] = useState(false);
  const [animationTime, setAnimationTime] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [animationSpeed, setAnimationSpeed] = useState(1.0);
  const [showStatistics, setShowStatistics] = useState(true);

  const animationRef = useRef();

  // 시뮬레이션 계산
  const runSimulation = useMemo(() => {
    return () => {
      setIsSimulating(true);

      setTimeout(() => {
        const {
          R_wafer, R_pad, rpm_pad, rpm_wafer, R_c, time_total, 
          num_time_steps, oscillation_enabled, oscillation_amplitude, 
          oscillation_speed, heatmap_grid_size
        } = params;

        // 각속도 계산
        const omega_pad = (rpm_pad * 2 * Math.PI) / 60;
        const omega_wafer = (rpm_wafer * 2 * Math.PI) / 60;

        // 시간 스텝
        const time_steps = Array.from({length: num_time_steps}, (_, i) => 
          (i * time_total) / (num_time_steps - 1)
        );
        const dt = time_steps[1] - time_steps[0];

        // 진동 계산
        let wafer_x_oscillation, wafer_vx_oscillation;
        if (oscillation_enabled) {
          const freq = oscillation_speed / (2 * oscillation_amplitude);
          wafer_x_oscillation = time_steps.map(t => 
            oscillation_amplitude * Math.sin(2 * Math.PI * freq * t)
          );
          wafer_vx_oscillation = time_steps.map(t => 
            (2 * Math.PI * freq) * oscillation_amplitude * Math.cos(2 * Math.PI * freq * t)
          );
        } else {
          wafer_x_oscillation = new Array(num_time_steps).fill(0);
          wafer_vx_oscillation = new Array(num_time_steps).fill(0);
        }

        // 웨이퍼 격자 생성 (히트맵 격자 크기 사용)
        const wafer_points = [];
        const grid_size = heatmap_grid_size;
        for (let i = 0; i < grid_size; i++) {
          for (let j = 0; j < grid_size; j++) {
            const x = (i - grid_size/2) * (2 * R_wafer / grid_size);
            const y = (j - grid_size/2) * (2 * R_wafer / grid_size);
            const r = Math.sqrt(x*x + y*y);

            if (r <= R_wafer) {
              wafer_points.push({
                x, y, r,
                zone: r <= R_wafer/3 ? 'center' : r <= 2*R_wafer/3 ? 'middle' : 'edge'
              });
            }
          }
        }

        // 사용자 정의 점들 추가
        customPoints.forEach((point, index) => {
          const x = point.radius * Math.cos(point.angle * Math.PI / 180);
          const y = point.radius * Math.sin(point.angle * Math.PI / 180);
          wafer_points.push({
            x, y, r: point.radius,
            zone: 'custom',
            customId: point.id
          });
        });

        // 궤적 계산
        const trajectories = wafer_points.map(point => {
          const trajectory = [];
          for (let t_idx = 0; t_idx < num_time_steps; t_idx++) {
            const t = time_steps[t_idx];

            // 웨이퍼 중심의 위치
            const wafer_center_x = wafer_x_oscillation[t_idx];
            const wafer_center_y = 0;

            // 웨이퍼 상의 점의 위치 (웨이퍼 중심 기준)
            const wafer_point_x = point.x * Math.cos(omega_wafer * t) - point.y * Math.sin(omega_wafer * t);
            const wafer_point_y = point.x * Math.sin(omega_wafer * t) + point.y * Math.cos(omega_wafer * t);

            // 절대 좌표
            const absolute_x = wafer_center_x + wafer_point_x;
            const absolute_y = wafer_center_y + wafer_point_y;

            // 패드 중심 기준 좌표
            const pad_x = absolute_x + R_c;
            const pad_y = absolute_y;

            // 패드 상의 상대 좌표
            const pad_relative_x = pad_x * Math.cos(omega_pad * t) + pad_y * Math.sin(omega_pad * t);
            const pad_relative_y = -pad_x * Math.sin(omega_pad * t) + pad_y * Math.cos(omega_pad * t);

            trajectory.push({
              t,
              x: pad_relative_x,
              y: pad_relative_y,
              vx: wafer_vx_oscillation[t_idx] + 
                   (-point.x * omega_wafer * Math.sin(omega_wafer * t) - point.y * omega_wafer * Math.cos(omega_wafer * t)),
              vy: point.x * omega_wafer * Math.cos(omega_wafer * t) - point.y * omega_wafer * Math.sin(omega_wafer * t)
            });
          }
          return trajectory;
        });

        // 거리 계산
        const finalDistances = trajectories.map(trajectory => {
          let totalDistance = 0;
          for (let i = 1; i < trajectory.length; i++) {
            const dx = trajectory[i].x - trajectory[i-1].x;
            const dy = trajectory[i].y - trajectory[i-1].y;
            totalDistance += Math.sqrt(dx*dx + dy*dy);
          }
          return totalDistance;
        });

        setResults({
          wafer_points,
          trajectories,
          finalDistances,
          time_steps
        });
        setIsSimulating(false);
      }, 100);
    };
  }, [params, customPoints]);

  // 애니메이션 제어
  useEffect(() => {
    if (isPlaying && results) {
      const animate = () => {
        setAnimationTime(prev => {
          const newTime = prev + animationSpeed * 0.016; // 60fps
          if (newTime >= params.time_total) {
            setIsPlaying(false);
            return params.time_total;
          }
          return newTime;
        });
        animationRef.current = requestAnimationFrame(animate);
      };
      animationRef.current = requestAnimationFrame(animate);
    } else {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    }

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [isPlaying, animationSpeed, results, params.time_total]);

  // 점 선택 처리
  const handlePointSelection = useCallback((pointIndex) => {
    if (isSelectingMode) {
      setUserSelectedPoints(prev => {
        if (prev.includes(pointIndex)) {
          return prev.filter(p => p !== pointIndex);
        } else if (prev.length < 10) {
          return [...prev, pointIndex];
        }
        return prev;
      });
    } else {
      setSelectedPoints(prev => {
        if (prev.includes(pointIndex)) {
          return prev.filter(p => p !== pointIndex);
        } else if (prev.length < 5) {
          return [...prev, pointIndex];
        }
        return prev;
      });
    }
  }, [isSelectingMode]);

  // 사용자 정의 점 추가
  const addCustomPoint = useCallback(() => {
    const newPoint = {
      id: Date.now(),
      radius: newPointRadius,
      angle: newPointAngle
    };
    setCustomPoints(prev => [...prev, newPoint]);
    setNewPointRadius(75);
    setNewPointAngle(0);
  }, [newPointRadius, newPointAngle]);

  // 사용자 정의 점 제거
  const removeCustomPoint = useCallback((pointId) => {
    setCustomPoints(prev => prev.filter(p => p.id !== pointId));
  }, []);

  // 매개변수 제어 패널
  const ControlPanel = () => (
    <div className="bg-white p-4 rounded-lg shadow-lg space-y-4">
      <h2 className="text-lg font-bold mb-3">시뮬레이션 매개변수</h2>

      {/* 기본 매개변수 */}
      <div className="grid grid-cols-8 gap-2 items-end">
        <div>
          <label className="block text-xs font-medium mb-1">웨이퍼R(mm)</label>
          <input
            type="number"
            value={params.R_wafer}
            onChange={(e) => setParams({...params, R_wafer: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">패드R(mm)</label>
          <input
            type="number"
            value={params.R_pad}
            onChange={(e) => setParams({...params, R_pad: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">패드RPM</label>
          <input
            type="number"
            value={params.rpm_pad}
            onChange={(e) => setParams({...params, rpm_pad: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">웨이퍼RPM</label>
          <input
            type="number"
            value={params.rpm_wafer}
            onChange={(e) => setParams({...params, rpm_wafer: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">시간(s)</label>
          <input
            type="number"
            value={params.time_total}
            onChange={(e) => setParams({...params, time_total: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">스텝수</label>
          <input
            type="number"
            value={params.num_time_steps}
            onChange={(e) => setParams({...params, num_time_steps: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">진동폭(mm)</label>
          <input
            type="number"
            value={params.oscillation_amplitude}
            onChange={(e) => setParams({...params, oscillation_amplitude: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div className="flex flex-col items-center">
          <label className="text-xs font-medium mb-1">진동</label>
          <input
            type="checkbox"
            checked={params.oscillation_enabled}
            onChange={(e) => setParams({...params, oscillation_enabled: e.target.checked})}
            className="w-4 h-4"
          />
        </div>
      </div>

      {/* 히트맵 격자 크기 설정 */}
      <div className="border-t pt-3">
        <h3 className="font-semibold text-sm mb-2">히트맵 설정</h3>
        <div className="flex items-center space-x-4">
          <div>
            <label className="block text-xs font-medium mb-1">격자 크기 ({params.heatmap_grid_size}x{params.heatmap_grid_size})</label>
            <input
              type="range"
              min="10"
              max="50"
              value={params.heatmap_grid_size}
              onChange={(e) => setParams({...params, heatmap_grid_size: Number(e.target.value)})}
              className="w-full"
            />
          </div>
          <div className="text-xs text-gray-600">
            총 점 수: ~{Math.floor(Math.PI * params.heatmap_grid_size * params.heatmap_grid_size / 4)}개
          </div>
        </div>
      </div>

      {/* 사용자 정의 점 입력 */}
      <div className="border-t pt-3">
        <h3 className="font-semibold text-sm mb-2">사용자 정의 점 (패드 좌표계용)</h3>
        <div className="flex items-center space-x-2 mb-2">
          <div>
            <label className="block text-xs font-medium mb-1">반지름(mm)</label>
            <input
              type="number"
              min="0"
              max={params.R_wafer}
              value={newPointRadius}
              onChange={(e) => setNewPointRadius(Number(e.target.value))}
              className="w-20 px-2 py-1 text-sm border rounded"
            />
          </div>
          <div>
            <label className="block text-xs font-medium mb-1">각도(°)</label>
            <input
              type="number"
              min="0"
              max="360"
              value={newPointAngle}
              onChange={(e) => setNewPointAngle(Number(e.target.value))}
              className="w-20 px-2 py-1 text-sm border rounded"
            />
          </div>
          <button
            onClick={addCustomPoint}
            className="px-3 py-1 bg-blue-500 text-white text-sm rounded hover:bg-blue-600"
          >
            추가
          </button>
        </div>
      </div>

      {/* 시뮬레이션 실행 버튼 */}
      <div className="border-t pt-3">
        <button
          onClick={runSimulation}
          disabled={isSimulating}
          className="w-full py-2 bg-green-500 text-white rounded hover:bg-green-600 disabled:bg-gray-400"
        >
          {isSimulating ? '시뮬레이션 중...' : '시뮬레이션 실행'}
        </button>
      </div>
    </div>
  );

  // 뷰 탭 컴포넌트
  const ViewTabs = () => (
    <div className="bg-white rounded-lg shadow-lg">
      <div className="flex border-b">
        {[
          { id: 'trajectory', label: '궤적 시각화' },
          { id: 'heatmap', label: '거리 히트맵' },
          { id: 'statistics', label: '통계 분석' },
          { id: 'animation', label: '애니메이션' }
        ].map(tab => (
          <button
            key={tab.id}
            onClick={() => setCurrentView(tab.id)}
            className={`flex-1 py-2 px-4 text-sm font-medium ${
              currentView === tab.id
                ? 'text-blue-600 border-b-2 border-blue-600'
                : 'text-gray-500 hover:text-gray-700'
            }`}
          >
            {tab.label}
          </button>
        ))}
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">CMP Sliding Simulator</h1>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* 왼쪽 패널 */}
          <div className="lg:col-span-1 space-y-4">
            <ControlPanel />

            {/* 선택된 점들 정보 */}
            {(selectedPoints.length > 0 || userSelectedPoints.length > 0) && (
              <div className="bg-white border rounded-lg p-4">
                <h3 className="text-lg font-semibold mb-2">선택된 점들 정보</h3>

                {selectedPoints.length > 0 && (
                  <div className="mb-4">
                    <h4 className="font-medium text-blue-600 mb-2">분석용 선택 점들 ({selectedPoints.length}/5)</h4>
                    <div className="grid grid-cols-5 gap-4">
                      {selectedPoints.map(pointIndex => {
                        const point = results?.wafer_points[pointIndex];
                        const finalDistance = results?.finalDistances[pointIndex];
                        return (
                          <div key={pointIndex} className="bg-blue-50 p-3 rounded">
                            <div className="font-medium">점 {pointIndex}</div>
                            <div className="text-sm">위치: ({point?.x.toFixed(1)}, {point?.y.toFixed(1)})</div>
                            <div className="text-sm">구역: {point?.zone}</div>
                            <div className="text-sm">반지름: {point?.r.toFixed(1)}mm</div>
                            <div className="text-sm">최종 거리: {finalDistance?.toFixed(2)}mm</div>
                            <button
                              onClick={() => handlePointSelection(pointIndex)}
                              className="mt-2 text-xs bg-red-500 text-white px-2 py-1 rounded"
                            >
                              제거
                            </button>
                          </div>
                        );
                      })}
                    </div>
                  </div>
                )}

                {userSelectedPoints.length > 0 && (
                  <div>
                    <h4 className="font-medium text-orange-600 mb-2">사용자 선택 점들 ({userSelectedPoints.length}/10)</h4>
                    <div className="grid grid-cols-5 gap-4">
                      {userSelectedPoints.map(pointIndex => {
                        const point = results?.wafer_points[pointIndex];
                        const finalDistance = results?.finalDistances[pointIndex];
                        return (
                          <div key={pointIndex} className="bg-orange-50 p-3 rounded">
                            <div className="font-medium">점 {pointIndex}</div>
                            <div className="text-sm">위치: ({point?.x.toFixed(1)}, {point?.y.toFixed(1)})</div>
                            <div className="text-sm">구역: {point?.zone}</div>
                            <div className="text-sm">반지름: {point?.r.toFixed(1)}mm</div>
                            <div className="text-sm">최종 거리: {finalDistance?.toFixed(2)}mm</div>
                            <button
                              onClick={() => {
                                setIsSelectingMode(true);
                                handlePointSelection(pointIndex);
                              }}
                              className="mt-2 text-xs bg-red-500 text-white px-2 py-1 rounded"
                            >
                              제거
                            </button>
                          </div>
                        );
                      })}
                    </div>
                  </div>
                )}

                {customPoints.length > 0 && (
                  <div>
                    <h4 className="font-medium text-purple-600 mb-2">사용자 정의 점들 ({customPoints.length}개)</h4>
                    <div className="grid grid-cols-5 gap-4">
                      {customPoints.map(point => {
                        const pointIndex = results?.wafer_points.findIndex(p => p.customId === point.id);
                        const finalDistance = pointIndex >= 0 ? results?.finalDistances[pointIndex] : 0;
                        const x = point.radius * Math.cos(point.angle * Math.PI / 180);
                        const y = point.radius * Math.sin(point.angle * Math.PI / 180);
                        return (
                          <div key={point.id} className="bg-purple-50 p-3 rounded">
                            <div className="font-medium">사용자 점 {point.id}</div>
                            <div className="text-sm">위치: ({x.toFixed(1)}, {y.toFixed(1)})</div>
                            <div className="text-sm">반지름: {point.radius}mm</div>
                            <div className="text-sm">각도: {point.angle}°</div>
                            <div className="text-sm">최종 거리: {finalDistance?.toFixed(2)}mm</div>
                            <button
                              onClick={() => removeCustomPoint(point.id)}
                              className="mt-2 text-xs bg-red-500 text-white px-2 py-1 rounded"
                            >
                              제거
                            </button>
                          </div>
                        );
                      })}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>

          {/* 오른쪽 메인 콘텐츠 */}
          <div className="lg:col-span-3 space-y-4">
            <ViewTabs />

            {/* 뷰 콘텐츠 */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              {currentView === 'trajectory' && (
                <div>
                  <h3 className="text-lg font-semibold mb-4">궤적 시각화</h3>
                  <p className="text-gray-600 mb-4">
                    웨이퍼 상의 점들이 패드 상에서 그리는 궤적을 시각화합니다.
                  </p>
                  {/* 궤적 시각화 컴포넌트가 여기에 들어갈 예정 */}
                </div>
              )}

              {currentView === 'heatmap' && (
                <div>
                  <h3 className="text-lg font-semibold mb-4">거리 히트맵</h3>
                  <p className="text-gray-600 mb-4">
                    웨이퍼 상의 각 점들이 이동한 총 거리를 히트맵으로 표시합니다.
                  </p>
                  {/* 히트맵 컴포넌트가 여기에 들어갈 예정 */}
                </div>
              )}

              {currentView === 'statistics' && (
                <div>
                  <h3 className="text-lg font-semibold mb-4">통계 분석</h3>
                  <p className="text-gray-600 mb-4">
                    시뮬레이션 결과에 대한 통계적 분석을 제공합니다.
                  </p>
                  {/* 통계 분석 컴포넌트가 여기에 들어갈 예정 */}
                </div>
              )}

              {currentView === 'animation' && (
                <div>
                  <h3 className="text-lg font-semibold mb-4">애니메이션</h3>
                  <p className="text-gray-600 mb-4">
                    시뮬레이션 과정을 애니메이션으로 재생합니다.
                  </p>
                  {/* 애니메이션 컴포넌트가 여기에 들어갈 예정 */}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdvancedWaferAnalysisSimulator;
