export default AdvancedWaferAnalysisSimulator;import React, { useState, useEffect, useMemo, useRef, useCallback } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import * as THREE from 'three';

const AdvancedWaferAnalysisSimulator = () => {
  // 시뮬레이션 파라미터
  const [params, setParams] = useState({
    R_wafer: 150,
    R_pad: 400,
    rpm_pad: 31,
    rpm_wafer: 30,
    R_c: 225,
    time_total: 150,
    num_time_steps: 500,
    oscillation_enabled: true,
    oscillation_amplitude: 40,
    oscillation_speed: 300/60,
    num_wafer_grid: 400,
    heatmap_grid_points: 300
  });

  const [currentView, setCurrentView] = useState('trajectory');
  const [isSimulating, setIsSimulating] = useState(false);
  const [results, setResults] = useState(null);
  const [selectedPoints, setSelectedPoints] = useState([]);
  const [userSelectedPoints, setUserSelectedPoints] = useState([]);
  const [customPoints, setCustomPoints] = useState([]);
  const [newPointRadius, setNewPointRadius] = useState(75);
  const [newPointAngle, setNewPointAngle] = useState(0);
  const [isSelectingMode, setIsSelectingMode] = useState(false);
  const [animationTime, setAnimationTime] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [animationSpeed, setAnimationSpeed] = useState(1.0);

  // 기본 추적 점들
  const defaultTrackingPoints = [
    { id: 'center', radius: 0, angle: 0, label: '중심 (0mm)' },
    { id: 'r50', radius: 50, angle: 0, label: '50mm' },
    { id: 'r100', radius: 100, angle: 0, label: '100mm' },
    { id: 'r150', radius: 150, angle: 0, label: '150mm' }
  ];

  const animationRef = useRef();

  // 시뮬레이션 계산
  const runSimulation = useMemo(() => {
    return () => {
      setIsSimulating(true);

      setTimeout(() => {
        const {
          R_wafer, R_pad, rpm_pad, rpm_wafer, R_c, time_total, 
          num_time_steps, oscillation_enabled, oscillation_amplitude, 
          oscillation_speed, num_wafer_grid
        } = params;

        // 각속도 계산
        const omega_pad = (rpm_pad * 2 * Math.PI) / 60;
        const omega_wafer = (rpm_wafer * 2 * Math.PI) / 60;

        // 시간 스텝
        const time_steps = Array.from({length: num_time_steps}, (_, i) => 
          (i * time_total) / (num_time_steps - 1)
        );
        const dt = time_steps[1] - time_steps[0];

        // 진동 계산
        let wafer_x_oscillation, wafer_vx_oscillation;
        if (oscillation_enabled) {
          const freq = oscillation_speed / (2 * oscillation_amplitude);
          wafer_x_oscillation = time_steps.map(t => 
            oscillation_amplitude * Math.sin(2 * Math.PI * freq * t)
          );
          wafer_vx_oscillation = time_steps.map(t => 
            (2 * Math.PI * freq) * oscillation_amplitude * Math.cos(2 * Math.PI * freq * t)
          );
        } else {
          wafer_x_oscillation = new Array(num_time_steps).fill(0);
          wafer_vx_oscillation = new Array(num_time_steps).fill(0);
        }

        // 웨이퍼 격자 생성
        const wafer_points = [];
        const radial_samples = Math.ceil(Math.sqrt(num_wafer_grid / 4));
        const angular_samples = Math.ceil(num_wafer_grid / radial_samples);

        wafer_points.push({x: 0, y: 0, r: 0, theta: 0, zone: 'center'});

        for (let i = 1; i <= radial_samples; i++) {
          const r = (i / radial_samples) * R_wafer;
          const num_theta = Math.max(6, Math.floor(angular_samples * i / radial_samples));

          for (let j = 0; j < num_theta; j++) {
            const theta = (2 * Math.PI * j) / num_theta;
            const x = r * Math.cos(theta);
            const y = r * Math.sin(theta);

            let zone = 'outer';
            if (r < R_wafer * 0.3) zone = 'inner';
            else if (r < R_wafer * 0.7) zone = 'middle';

            wafer_points.push({x, y, r, theta, zone});
          }
        }

        // 히트맵용 웨이퍼 점들 (150mm부터 300개 점)
        const heatmap_wafer_points = [];
        const target_points = 300;
        const min_radius = 150;
        const max_radius = R_wafer;
        const num_rings = 10;
        let points_added = 0;

        for (let ring = 0; ring < num_rings && points_added < target_points; ring++) {
          const r = min_radius + (ring / (num_rings - 1)) * (max_radius - min_radius);
          const circumference = 2 * Math.PI * r;
          const points_in_ring = Math.max(8, Math.round(circumference / (R_wafer / 12)));

          for (let i = 0; i < points_in_ring && points_added < target_points; i++) {
            const theta = (2 * Math.PI * i) / points_in_ring;
            const x = r * Math.cos(theta);
            const y = r * Math.sin(theta);

            let zone = 'outer';
            if (r < R_wafer * 0.9) zone = 'middle';

            heatmap_wafer_points.push({x, y, r, theta, zone});
            points_added++;
          }
        }

        // 추적 점들 추가
        const allTrackingPoints = [...defaultTrackingPoints, ...customPoints];
        allTrackingPoints.forEach(point => {
          const x = point.radius * Math.cos(point.angle * Math.PI / 180);
          const y = point.radius * Math.sin(point.angle * Math.PI / 180);
          if (Math.sqrt(x * x + y * y) <= R_wafer) {
            wafer_points.push({
              x: x, 
              y: y, 
              r: point.radius, 
              theta: point.angle * Math.PI / 180, 
              zone: point.id ? 'default' : 'custom',
              isCustom: !point.id,
              isDefault: !!point.id,
              customId: point.id,
              label: point.label || `사용자점 ${point.id}`
            });
          }
        });

        const num_actual_grid = wafer_points.length;
        const wafer_area = Math.PI * R_wafer * R_wafer;
        const cell_area = wafer_area / num_actual_grid;

        // 데이터 저장소
        const heatmap_trajectoryData = heatmap_wafer_points.map(() => []);
        const heatmap_cumulativeDistance = heatmap_wafer_points.map(() => 0);
        const timeSeriesData = [];
        const trajectoryData = wafer_points.map(() => []);
        const cumulativeDistance = wafer_points.map(() => 0);
        const padTrajectoryData = wafer_points.map(() => []);

        // 시뮬레이션 메인 루프
        for (let k = 0; k < num_time_steps; k++) {
          const t = time_steps[k];
          const pad_theta = omega_pad * t;
          const wafer_theta = omega_wafer * t;

          const wafer_xc = R_c + wafer_x_oscillation[k];
          const wafer_yc = 0.0;

          const cos_w = Math.cos(wafer_theta);
          const sin_w = Math.sin(wafer_theta);
          const cos_p = Math.cos(-pad_theta);
          const sin_p = Math.sin(-pad_theta);

          const timeStepData = {
            time: t,
            wafer_center_x: wafer_xc,
            wafer_center_y: wafer_yc,
            wafer_angle: wafer_theta,
            pad_angle: pad_theta,
            points: []
          };

          // 일반 웨이퍼 점들 계산
          for (let i = 0; i < num_actual_grid; i++) {
            const {x: wx, y: wy, zone} = wafer_points[i];

            const abs_x = wafer_xc + (wx * cos_w - wy * sin_w);
            const abs_y = wafer_yc + (wx * sin_w + wy * cos_w);

            const pad_local_x = abs_x * cos_p - abs_y * sin_p;
            const pad_local_y = abs_x * sin_p + abs_y * cos_p;

            const v_center_x = wafer_vx_oscillation[k];
            const v_center_y = 0.0;
            const v_local_x = -omega_wafer * wx * sin_w - omega_wafer * wy * cos_w;
            const v_local_y = omega_wafer * wx * cos_w - omega_wafer * wy * sin_w;
            const v_gx = v_center_x + v_local_x;
            const v_gy = v_center_y + v_local_y;

            const v_pad_x = -omega_pad * abs_y;
            const v_pad_y = omega_pad * abs_x;

            const v_rel_x = v_gx - v_pad_x;
            const v_rel_y = v_gy - v_pad_y;
            const speed_rel = Math.sqrt(v_rel_x * v_rel_x + v_rel_y * v_rel_y);

            if (k > 0) {
              cumulativeDistance[i] += speed_rel * dt;
            }

            padTrajectoryData[i].push({
              time: t,
              x: pad_local_x,
              y: pad_local_y,
              abs_x: abs_x,
              abs_y: abs_y
            });

            trajectoryData[i].push({
              time: t,
              x: abs_x,
              y: abs_y,
              local_x: wx,
              local_y: wy,
              speed: speed_rel,
              cumulative_distance: cumulativeDistance[i],
              zone: zone
            });

            timeStepData.points.push({
              id: i,
              x: abs_x,
              y: abs_y,
              local_x: wx,
              local_y: wy,
              speed: speed_rel,
              cumulative_distance: cumulativeDistance[i],
              zone: zone
            });
          }

          // 히트맵용 점들 계산
          for (let i = 0; i < heatmap_wafer_points.length; i++) {
            const {x: wx, y: wy, zone} = heatmap_wafer_points[i];

            const abs_x = wafer_xc + (wx * cos_w - wy * sin_w);
            const abs_y = wafer_yc + (wx * sin_w + wy * cos_w);

            const v_center_x = wafer_vx_oscillation[k];
            const v_center_y = 0.0;
            const v_local_x = -omega_wafer * wx * sin_w - omega_wafer * wy * cos_w;
            const v_local_y = omega_wafer * wx * cos_w - omega_wafer * wy * sin_w;
            const v_gx = v_center_x + v_local_x;
            const v_gy = v_center_y + v_local_y;

            const v_pad_x = -omega_pad * abs_y;
            const v_pad_y = omega_pad * abs_x;

            const v_rel_x = v_gx - v_pad_x;
            const v_rel_y = v_gy - v_pad_y;
            const speed_rel = Math.sqrt(v_rel_x * v_rel_x + v_rel_y * v_rel_y);

            if (k > 0) {
              heatmap_cumulativeDistance[i] += speed_rel * dt;
            }

            heatmap_trajectoryData[i].push({
              time: t,
              x: abs_x,
              y: abs_y,
              local_x: wx,
              local_y: wy,
              speed: speed_rel,
              cumulative_distance: heatmap_cumulativeDistance[i],
              zone: zone
            });
          }

          timeSeriesData.push(timeStepData);
        }

        // 통계 계산
        const finalDistances = cumulativeDistance;
        const maxDistance = Math.max(...finalDistances);
        const minDistance = Math.min(...finalDistances);
        const avgDistance = finalDistances.reduce((a, b) => a + b, 0) / finalDistances.length;
        const uniformityIndex = maxDistance > 0 ? 1 - (maxDistance - minDistance) / maxDistance : 1;

        setResults({
          wafer_points,
          heatmap_wafer_points,
          heatmap_trajectoryData,
          heatmap_cumulativeDistance,
          timeSeriesData,
          trajectoryData,
          padTrajectoryData,
          finalDistances,
          statistics: {
            maxDistance,
            minDistance,
            avgDistance,
            uniformityIndex
          },
          R_wafer,
          R_pad,
          time_steps,
          cell_area
        });

        setIsSimulating(false);
      }, 100);
    };
  }, [params, customPoints]);

  useEffect(() => {
    runSimulation();
  }, [runSimulation]);

  // 사용자 정의 점 함수들
  const addCustomPoint = () => {
    if (newPointRadius <= params.R_wafer && newPointRadius >= 0) {
      const newPoint = {
        id: Date.now(),
        radius: newPointRadius,
        angle: newPointAngle
      };
      setCustomPoints(prev => [...prev, newPoint]);
    }
  };

  const removeCustomPoint = (pointId) => {
    setCustomPoints(prev => prev.filter(p => p.id !== pointId));
  };

  // 애니메이션 제어
  const toggleAnimation = useCallback(() => {
    if (isPlaying) {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
        animationRef.current = null;
      }
      setIsPlaying(false);
    } else {
      if (!results || !results.time_steps) return;
      setIsPlaying(true);
    }
  }, [isPlaying, results]);

  const forceStopAnimation = useCallback(() => {
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
      animationRef.current = null;
    }
    setIsPlaying(false);
  }, []);

  useEffect(() => {
    let isCurrentlyRunning = false;

    if (isPlaying && results && results.time_steps) {
      isCurrentlyRunning = true;

      const animate = () => {
        if (!isCurrentlyRunning || !isPlaying) {
          if (animationRef.current) {
            cancelAnimationFrame(animationRef.current);
            animationRef.current = null;
          }
          return;
        }

        setAnimationTime(prev => {
          const next = prev + (0.5 * animationSpeed);
          const maxTime = results.time_steps.length - 1;

          if (next >= maxTime) {
            isCurrentlyRunning = false;
            setIsPlaying(false);
            return maxTime;
          }
          return next;
        });

        if (isCurrentlyRunning && isPlaying) {
          animationRef.current = requestAnimationFrame(animate);
        }
      };

      animationRef.current = requestAnimationFrame(animate);

      return () => {
        isCurrentlyRunning = false;
        if (animationRef.current) {
          cancelAnimationFrame(animationRef.current);
          animationRef.current = null;
        }
      };
    }
  }, [isPlaying, animationSpeed, results]);

  const resetAnimation = useCallback(() => {
    forceStopAnimation();
    setAnimationTime(0);
  }, [forceStopAnimation]);

  useEffect(() => {
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
        animationRef.current = null;
      }
    };
  }, []);

  // 포인트 선택 핸들러
  const handlePointSelection = (pointIndex) => {
    if (isSelectingMode) {
      setUserSelectedPoints(prev => {
        if (prev.includes(pointIndex)) {
          return prev.filter(p => p !== pointIndex);
        } else {
          return [...prev, pointIndex].slice(-10);
        }
      });
    } else {
      setSelectedPoints(prev => {
        if (prev.includes(pointIndex)) {
          return prev.filter(p => p !== pointIndex);
        } else {
          return [...prev, pointIndex].slice(-5);
        }
      });
    }
  };

  // 3D 표면 시각화
  const Surface3D = ({ data, points, title, radius }) => {
    const mountRef = useRef(null);
    const rendererRef = useRef(null);

    useEffect(() => {
      if (!data || !points || !mountRef.current) return;

      if (rendererRef.current) {
        mountRef.current.removeChild(rendererRef.current.domElement);
        rendererRef.current.dispose();
      }

      const scene = new THREE.Scene();
      scene.background = new THREE.Color(0xf0f0f0);

      const camera = new THREE.PerspectiveCamera(75, 400/300, 0.1, 1000);
      const renderer = new THREE.WebGLRenderer({ antialias: true });
      renderer.setSize(400, 300);

      mountRef.current.appendChild(renderer.domElement);
      rendererRef.current = renderer;

      const ambientLight = new THREE.AmbientLight(0x404040, 0.6);
      scene.add(ambientLight);

      const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
      directionalLight.position.set(200, 200, 200);
      scene.add(directionalLight);

      const maxValue = Math.max(...data);
      const minValue = Math.min(...data);
      const range = maxValue - minValue || 1;

      const geometry = new THREE.BufferGeometry();
      const vertices = [];
      const colors = [];

      points.forEach((point, i) => {
        const normalizedValue = (data[i] - minValue) / range;
        const z = normalizedValue * radius * 0.3;

        vertices.push(point.x, point.y, z);

        const hue = (1 - normalizedValue) * 240;
        const color = new THREE.Color().setHSL(hue / 360, 0.8, 0.5);
        colors.push(color.r, color.g, color.b);
      });

      geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
      geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

      const material = new THREE.PointsMaterial({ 
        vertexColors: true,
        size: 3,
        sizeAttenuation: false
      });

      const mesh = new THREE.Points(geometry, material);
      scene.add(mesh);

      camera.position.set(radius * 1.5, radius * 1.2, radius * 1.5);
      camera.lookAt(0, 0, 0);

      const animate = () => {
        requestAnimationFrame(animate);
        mesh.rotation.z += 0.005;
        renderer.render(scene, camera);
      };

      animate();
    }, [data, points, radius]);

    return (
      <div className="w-full bg-white border rounded-lg p-4">
        <h3 className="text-lg font-semibold mb-4">{title}</h3>
        <div className="flex justify-center">
          <div ref={mountRef} className="border rounded" />
        </div>
      </div>
    );
  };

  // 매개변수 제어 패널
  const ControlPanel = () => (
    <div className="bg-white p-4 rounded-lg shadow-lg space-y-4">
      <h2 className="text-lg font-bold mb-3">시뮬레이션 매개변수</h2>

      <div className="grid grid-cols-8 gap-2 items-end">
        <div>
          <label className="block text-xs font-medium mb-1">웨이퍼R(mm)</label>
          <input
            type="number"
            value={params.R_wafer}
            onChange={(e) => setParams({...params, R_wafer: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">패드R(mm)</label>
          <input
            type="number"
            value={params.R_pad}
            onChange={(e) => setParams({...params, R_pad: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">패드RPM</label>
          <input
            type="number"
            value={params.rpm_pad}
            onChange={(e) => setParams({...params, rpm_pad: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">웨이퍼RPM</label>
          <input
            type="number"
            value={params.rpm_wafer}
            onChange={(e) => setParams({...params, rpm_wafer: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">시간(s)</label>
          <input
            type="number"
            value={params.time_total}
            onChange={(e) => setParams({...params, time_total: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">스텝수</label>
          <input
            type="number"
            value={params.num_time_steps}
            onChange={(e) => setParams({...params, num_time_steps: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">진동폭(mm)</label>
          <input
            type="number"
            value={params.oscillation_amplitude}
            onChange={(e) => setParams({...params, oscillation_amplitude: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div className="flex flex-col items-center">
          <label className="text-xs font-medium mb-1">진동</label>
          <input
            type="checkbox"
            checked={params.oscillation_enabled}
            onChange={(e) => setParams({...params, oscillation_enabled: e.target.checked})}
            className="w-4 h-4"
          />
        </div>
      </div>

      <div className="border-t pt-3">
        <h3 className="font-semibold text-sm mb-2">사용자 정의 점</h3>
        <div className="flex items-center space-x-2 mb-2">
          <div>
            <label className="block text-xs font-medium mb-1">반지름(mm)</label>
            <input
              type="number"
              min="0"
              max={params.R_wafer}
              value={newPointRadius}
              onChange={(e) => setNewPointRadius(Number(e.target.value))}
              className="w-20 px-2 py-1 text-sm border rounded"
            />
          </div>
          <div>
            <label className="block text-xs font-medium mb-1">각도(°)</label>
            <input
              type="number"
              min="0"
              max="360"
              value={newPointAngle}
              onChange={(e) => setNewPointAngle(Number(e.target.value))}
              className="w-20 px-2 py-1 text-sm border rounded"
            />
          </div>
          <button
            onClick={addCustomPoint}
            className="px-3 py-1 bg-green-500 text-white text-sm rounded hover:bg-green-600"
          >
            추가
          </button>
        </div>

        {customPoints.length > 0 && (
          <div className="max-h-32 overflow-y-auto">
            <h4 className="text-xs font-medium mb-1">추가된 점들:</h4>
            {customPoints.map(point => (
              <div key={point.id} className="flex items-center justify-between bg-purple-50 px-2 py-1 rounded text-xs mb-1">
                <span>R: {point.radius}mm, θ: {point.angle}°</span>
                <button
                  onClick={() => removeCustomPoint(point.id)}
                  className="text-red-500 hover:text-red-700"
                >
                  삭제
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      <button
        onClick={runSimulation}
        disabled={isSimulating}
        className="w-full bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 disabled:bg-gray-400 text-sm"
      >
        {isSimulating ? '시뮬레이션 중...' : '시뮬레이션 실행'}
      </button>
    </div>
  );

  // 실시간 애니메이션 뷰
  const AnimationView = () => {
    if (!results) return null;

    const currentTimeIndex = Math.floor(animationTime);
    const currentData = results.timeSeriesData[currentTimeIndex];

    if (!currentData) return null;

    // 부드러운 곡선 생성 함수
    const createSmoothPath = (points, isUpper = true) => {
      if (points.length < 2) return '';

      let path = `M ${points[0].x} ${isUpper ? -points[0].y : points[0].y}`;

      for (let i = 1; i < points.length; i++) {
        const prev = points[i - 1];
        const curr = points[i];

        if (i === 1) {
          path += ` Q ${prev.x} ${isUpper ? -prev.y : prev.y} ${curr.x} ${isUpper ? -curr.y : curr.y}`;
        } else {
          const cpx = prev.x + (curr.x - prev.x) * 0.8;
          const cpy = prev.y + (curr.y - prev.y) * 0.8;
          path += ` Q ${cpx} ${isUpper ? -cpy : cpy} ${curr.x} ${isUpper ? -curr.y : curr.y}`;
        }
      }

      return path;
    };

    return (
      <div className="space-y-6">
        {/* 제어 패널 */}
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-xl font-semibold">실시간 시뮬레이션</h3>
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <button
                onClick={toggleAnimation}
                className={`px-4 py-2 rounded ${isPlaying ? 'bg-red-500 text-white' : 'bg-green-500 text-white'}`}
              >
                {isPlaying ? '⏸️ 일시정지' : '▶️ 재생'}
              </button>
              <button
                onClick={resetAnimation}
                className="px-4 py-2 rounded bg-gray-500 text-white hover:bg-gray-600"
              >
                🔄 리셋
              </button>
            </div>

            <div className="flex items-center space-x-2">
              <label className="text-sm font-medium">속도:</label>
              <select
                value={animationSpeed}
                onChange={(e) => setAnimationSpeed(Number(e.target.value))}
                className="px-2 py-1 border rounded text-sm"
              >
                <option value={0.25}>0.25x</option>
                <option value={0.5}>0.5x</option>
                <option value={1.0}>1.0x</option>
                <option value={1.5}>1.5x</option>
                <option value={2.0}>2.0x</option>
                <option value={3.0}>3.0x</option>
                <option value={5.0}>5.0x</option>
              </select>
            </div>

            <div className="flex items-center space-x-2">
              <span className="text-sm">시간: {currentData.time.toFixed(2)}s</span>
              <input
                type="range"
                min="0"
                max={(results.time_steps.length - 1)}
                value={animationTime}
                onChange={(e) => {
                  const newTime = Number(e.target.value);
                  if (animationRef.current) {
                    cancelAnimationFrame(animationRef.current);
                    animationRef.current = null;
                  }
                  setIsPlaying(false);
                  setAnimationTime(newTime);
                }}
                className="w-32"
              />
              <span className="text-xs text-gray-500">
                {Math.floor(animationTime)}/{results.time_steps.length - 1}
              </span>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-3 gap-4">
          {/* 1. 패드 좌표계 뷰 */}
          <div className="bg-white border rounded-lg p-4">
            <h4 className="text-lg font-semibold mb-4">패드 좌표계 (대칭 웨이퍼)</h4>
            <div className="relative w-full h-80 bg-gray-50 border">
              <svg width="100%" height="100%" viewBox={`${-results.R_pad*1.2} ${-results.R_pad*1.2} ${results.R_pad*2.4} ${results.R_pad*2.4}`}>
                {/* 회전하는 패드 */}
                <g transform={`rotate(${currentData.pad_angle * 180 / Math.PI})`}>
                  <circle
                    cx="0"
                    cy="0"
                    r={results.R_pad}
                    fill="lightgray"
                    opacity="0.3"
                    stroke="gray"
                    strokeWidth="2"
                  />
                  <line x1="0" y1="0" x2={results.R_pad * 0.8} y2="0" stroke="gray" strokeWidth="3" opacity="0.6"/>
                  <circle cx={results.R_pad * 0.8} cy="0" r="5" fill="gray" opacity="0.8"/>
                </g>
                <text x="0" y={results.R_pad + 30} textAnchor="middle" fontSize="12" fill="gray">패드 (회전)</text>

                {/* 상단 웨이퍼 */}
                <g transform={`translate(${currentData.wafer_center_x}, ${currentData.wafer_center_y})`}>
                  <circle cx="0" cy="0" r={results.R_wafer} fill="none" stroke="blue" strokeWidth="2" opacity="0.4" />
                  <g transform={`rotate(${currentData.wafer_angle * 180 / Math.PI})`}>
                    <line x1="0" y1="0" x2={results.R_wafer * 0.8} y2="0" stroke="blue" strokeWidth="3" opacity="0.7"/>
                    <circle cx={results.R_wafer * 0.8} cy="0" r="4" fill="blue" opacity="0.9"/>
                  </g>
                </g>
                <text x={currentData.wafer_center_x} y={currentData.wafer_center_y - results.R_wafer - 15} textAnchor="middle" fontSize="10" fill="blue">웨이퍼1</text>

                {/* 하단 웨이퍼 */}
                <g transform={`translate(${currentData.wafer_center_x}, ${-currentData.wafer_center_y})`}>
                  <circle cx="0" cy="0" r={results.R_wafer} fill="none" stroke="darkblue" strokeWidth="2" opacity="0.4" />
                  <g transform={`rotate(${currentData.wafer_angle * 180 / Math.PI})`}>
                    <line x1="0" y1="0" x2={results.R_wafer * 0.8} y2="0" stroke="darkblue" strokeWidth="3" opacity="0.7"/>
                    <circle cx={results.R_wafer * 0.8} cy="0" r="4" fill="darkblue" opacity="0.9"/>
                  </g>
                </g>
                <text x={currentData.wafer_center_x} y={-currentData.wafer_center_y + results.R_wafer + 25} textAnchor="middle" fontSize="10" fill="darkblue">웨이퍼2</text>

                {/* 기본 추적 점들의 자취선 */}
                {defaultTrackingPoints.map(point => {
                  const pointIndex = results.wafer_points.findIndex(p => p.customId === point.id);
                  if (pointIndex === -1) return null;

                  const trajectorySegments = results.padTrajectoryData[pointIndex]
                    .slice(0, currentTimeIndex + 1)
                    .filter(trajPoint => {
                      return Math.sqrt(trajPoint.x * trajPoint.x + trajPoint.y * trajPoint.y) <= results.R_pad;
                    });

                  if (trajectorySegments.length < 2) return null;

                  const colors = { 'center': 'red', 'r50': 'orange', 'r100': 'green', 'r150': 'blue' };

                  return (
                    <g key={`default-trail-${point.id}`}>
                      <path
                        d={createSmoothPath(trajectorySegments, true)}
                        fill="none"
                        stroke={colors[point.id] || 'black'}
                        strokeWidth="2"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="0.8"
                      />
                      <path
                        d={createSmoothPath(trajectorySegments, false)}
                        fill="none"
                        stroke={colors[point.id] || 'black'}
                        strokeWidth="2"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="0.8"
                        strokeDasharray="3,3"
                      />
                      {currentTimeIndex < trajectorySegments.length && (
                        <>
                          <circle
                            cx={trajectorySegments[trajectorySegments.length - 1].x}
                            cy={-trajectorySegments[trajectorySegments.length - 1].y}
                            r="4"
                            fill={colors[point.id] || 'black'}
                            stroke="white"
                            strokeWidth="2"
                          />
                          <circle
                            cx={trajectorySegments[trajectorySegments.length - 1].x}
                            cy={trajectorySegments[trajectorySegments.length - 1].y}
                            r="4"
                            fill={colors[point.id] || 'black'}
                            stroke="white"
                            strokeWidth="2"
                          />
                        </>
                      )}
                    </g>
                  );
                })}

                {/* 사용자 정의 점들의 자취선 */}
                {customPoints.map(point => {
                  const pointIndex = results.wafer_points.findIndex(p => p.customId === point.id);
                  if (pointIndex === -1) return null;

                  const trajectorySegments = results.padTrajectoryData[pointIndex]
                    .slice(0, currentTimeIndex + 1)
                    .filter(trajPoint => {
                      return Math.sqrt(trajPoint.x * trajPoint.x + trajPoint.y * trajPoint.y) <= results.R_pad;
                    });

                  if (trajectorySegments.length < 2) return null;

                  return (
                    <g key={`custom-trail-${point.id}`}>
                      <path
                        d={createSmoothPath(trajectorySegments, true)}
                        fill="none"
                        stroke="purple"
                        strokeWidth="3"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="1"
                      />
                      <path
                        d={createSmoothPath(trajectorySegments, false)}
                        fill="none"
                        stroke="purple"
                        strokeWidth="3"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="1"
                        strokeDasharray="5,5"
                      />
                      {currentTimeIndex < trajectorySegments.length && (
                        <>
                          <circle
                            cx={trajectorySegments[trajectorySegments.length - 1].x}
                            cy={-trajectorySegments[trajectorySegments.length - 1].y}
                            r="6"
                            fill="purple"
                            stroke="darkmagenta"
                            strokeWidth="3"
                          />
                          <circle
                            cx={trajectorySegments[trajectorySegments.length - 1].x}
                            cy={trajectorySegments[trajectorySegments.length - 1].y}
                            r="6"
                            fill="purple"
                            stroke="darkmagenta"
                            strokeWidth="3"
                          />
                        </>
                      )}
                    </g>
                  );
                })}

                {/* 범례 */}
                <g transform="translate(-400, -350)">
                  <rect x="0" y="0" width="140" height="120" fill="white" opacity="0.9" stroke="gray" strokeWidth="1" rx="5"/>
                  <text x="5" y="15" fontSize="10" fontWeight="bold" fill="black">추적점 범례</text>
                  <line x1="5" y1="25" x2="25" y2="25" stroke="red" strokeWidth="2"/>
                  <text x="30" y="30" fontSize="9" fill="black">중심 (0mm)</text>
                  <line x1="5" y1="40" x2="25" y2="40" stroke="orange" strokeWidth="2"/>
                  <text x="30" y="45" fontSize="9" fill="black">50mm</text>
                  <line x1="5" y1="55" x2="25" y2="55" stroke="green" strokeWidth="2"/>
                  <text x="30" y="60" fontSize="9" fill="black">100mm</text>
                  <line x1="5" y1="70" x2="25" y2="70" stroke="blue" strokeWidth="2"/>
                  <text x="30" y="75" fontSize="9" fill="black">150mm</text>
                  <line x1="5" y1="85" x2="25" y2="85" stroke="purple" strokeWidth="3"/>
                  <text x="30" y="90" fontSize="9" fill="black">사용자 정의</text>
                  <line x1="5" y1="100" x2="25" y2="100" stroke="black" strokeWidth="2" strokeDasharray="3,3"/>
                  <text x="30" y="105" fontSize="9" fill="black">하단 웨이퍼</text>
                </g>
              </svg>
            </div>
            <p className="text-xs text-gray-600 mt-2">대칭 웨이퍼들의 추적점이 회전 패드 위에 그리는 자취</p>
          </div>

          {/* 2. 웨이퍼 좌표계 뷰 - 히트맵 */}
          <div className="bg-white border rounded-lg p-4">
            <h4 className="text-lg font-semibold mb-4">웨이퍼 좌표계 (히트맵)</h4>
            <div className="relative w-full h-80 bg-gray-50 border">
              <svg width="100%" height="100%" viewBox={`${-results.R_wafer*1.2} ${-results.R_wafer*1.2} ${results.R_wafer*2.4} ${results.R_wafer*2.4}`}>
                <circle cx="0" cy="0" r={results.R_wafer} fill="none" stroke="blue" strokeWidth="3" />
                <line x1="0" y1="0" x2={results.R_wafer * 0.8} y2="0" stroke="blue" strokeWidth="3" opacity="0.6"/>
                <circle cx={results.R_wafer * 0.8} cy="0" r="4" fill="blue" opacity="0.8"/>
                <text x="0" y={results.R_wafer + 30} textAnchor="middle" fontSize="12" fill="blue">웨이퍼 (고정)</text>

                {/* 히트맵 점들 */}
                {results.heatmap_wafer_points.map((point, i) => {
                  const currentDistance = currentTimeIndex < results.heatmap_trajectoryData[i].length ? 
                    results.heatmap_trajectoryData[i][currentTimeIndex].cumulative_distance : 0;

                  const allCurrentDistances = results.heatmap_wafer_points.map((_, idx) => 
                    currentTimeIndex < results.heatmap_trajectoryData[idx].length ? 
                    results.heatmap_trajectoryData[idx][currentTimeIndex].cumulative_distance : 0
                  );
                  const maxCurrentDistance = Math.max(...allCurrentDistances);
                  const minCurrentDistance = Math.min(...allCurrentDistances);
                  const range = maxCurrentDistance - minCurrentDistance;

                  const intensity = range > 0 ? (currentDistance - minCurrentDistance) / range : 0;
                  const pointSize = Math.max(2, 2 + intensity * 6);
                  const hue = (1 - intensity) * 240;
                  const saturation = 80 + intensity * 20;
                  const lightness = 30 + intensity * 40;

                  return (
                    <circle
                      key={`heatmap-${i}`}
                      cx={point.x}
                      cy={-point.y}
                      r={pointSize}
                      fill={`hsl(${hue}, ${saturation}%, ${lightness}%)`}
                      opacity="0.8"
                      stroke={intensity > 0.8 ? 'white' : 'none'}
                      strokeWidth="0.5"
                    />
                  );
                })}

                <circle cx="0" cy="0" r="150" fill="none" stroke="lightgray" strokeWidth="2" strokeDasharray="5,5" opacity="0.7" />
                <text x="0" y="160" textAnchor="middle" fontSize="10" fill="gray">150mm</text>

                {/* 색상 범례 */}
                <g transform="translate(120, -140)">
                  <rect x="0" y="0" width="100" height="80" fill="white" opacity="0.9" stroke="gray" strokeWidth="1" rx="3"/>
                  <text x="5" y="15" fontSize="10" fontWeight="bold" fill="black">누적거리 (mm)</text>

                  <defs>
                    <linearGradient id="heatmapGradient" x1="0%" y1="0%" x2="100%" y2="0%">
                      <stop offset="0%" stopColor="hsl(240, 80%, 50%)" />
                      <stop offset="50%" stopColor="hsl(120, 80%, 50%)" />
                      <stop offset="100%" stopColor="hsl(0, 80%, 50%)" />
                    </linearGradient>
                  </defs>
                  <rect x="5" y="20" width="80" height="10" fill="url(#heatmapGradient)" stroke="gray" strokeWidth="0.5"/>

                  {(() => {
                    const allCurrentDistances = results.heatmap_wafer_points.map((_, idx) => 
                      currentTimeIndex < results.heatmap_trajectoryData[idx].length ? 
                      results.heatmap_trajectoryData[idx][currentTimeIndex].cumulative_distance : 0
                    );
                    const maxDist = Math.max(...allCurrentDistances);
                    const minDist = Math.min(...allCurrentDistances);

                    return (
                      <>
                        <text x="5" y="45" fontSize="8" fill="black">{minDist.toFixed(1)}</text>
                        <text x="45" y="45" fontSize="8" fill="black" textAnchor="middle">{((minDist + maxDist) / 2).toFixed(1)}</text>
                        <text x="85" y="45" fontSize="8" fill="black" textAnchor="end">{maxDist.toFixed(1)}</text>
                        <text x="5" y="70" fontSize="8" fill="gray">시간: {currentData.time.toFixed(1)}s</text>
                      </>
                    );
                  })()}
                </g>
              </svg>
            </div>
            <p className="text-xs text-gray-600 mt-2">고정된 웨이퍼 위 300개 점의 누적 이동거리 히트맵</p>
          </div>

          {/* 3. 웨이퍼 좌표계 뷰 - 패드 200mm 자취 */}
          <div className="bg-white border rounded-lg p-4">
            <h4 className="text-lg font-semibold mb-4">웨이퍼 좌표계 (패드 200mm점 자취)</h4>
            <div className="relative w-full h-80 bg-gray-50 border">
              <svg width="100%" height="100%" viewBox={`${-results.R_wafer*1.2} ${-results.R_wafer*1.2} ${results.R_wafer*2.4} ${results.R_wafer*2.4}`}>
                <circle cx="0" cy="0" r={results.R_wafer} fill="none" stroke="blue" strokeWidth="3" />
                <line x1="0" y1="0" x2={results.R_wafer * 0.8} y2="0" stroke="blue" strokeWidth="3" opacity="0.6"/>
                <circle cx={results.R_wafer * 0.8} cy="0" r="4" fill="blue" opacity="0.8"/>
                <text x="0" y={results.R_wafer + 30} textAnchor="middle" fontSize="12" fill="blue">웨이퍼 (고정)</text>

                {/* 패드 200mm 지점 자취 */}
                {(() => {
                  const padPoint = { x: 200, y: 0 };

                  const waferTrajectory = results.timeSeriesData.slice(0, currentTimeIndex + 1).map(data => {
                    const cos_p = Math.cos(data.pad_angle);
                    const sin_p = Math.sin(data.pad_angle);
                    const global_x = padPoint.x * cos_p - padPoint.y * sin_p;
                    const global_y = padPoint.x * sin_p + padPoint.y * cos_p;

                    const rel_x = global_x - data.wafer_center_x;
                    const rel_y = global_y - data.wafer_center_y;
                    const cos_w = Math.cos(-data.wafer_angle);
                    const sin_w = Math.sin(-data.wafer_angle);
                    const wafer_x = rel_x * cos_w - rel_y * sin_w;
                    const wafer_y = rel_x * sin_w + rel_y * cos_w;

                    return { x: wafer_x, y: wafer_y, time: data.time };
                  }).filter(point => 
                    Math.sqrt(point.x * point.x + point.y * point.y) <= results.R_wafer
                  );

                  if (waferTrajectory.length < 2) return null;

                  const createSmoothWaferPath = (points) => {
                    if (points.length < 2) return '';
                    let path = `M ${points[0].x} ${-points[0].y}`;
                    for (let i = 1; i < points.length; i++) {
                      const prev = points[i - 1];
                      const curr = points[i];
                      if (i === 1) {
                        path += ` Q ${prev.x} ${-prev.y} ${curr.x} ${-curr.y}`;
                      } else {
                        const cpx = prev.x + (curr.x - prev.x) * 0.7;
                        const cpy = prev.y + (curr.y - prev.y) * 0.7;
                        path += ` Q ${cpx} ${-cpy} ${curr.x} ${-curr.y}`;
                      }
                    }
                    return path;
                  };

                  return (
                    <g key="pad-200mm-trail">
                      <path
                        d={createSmoothWaferPath(waferTrajectory)}
                        fill="none"
                        stroke="darkorange"
                        strokeWidth="3"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="0.8"
                      />
                      <path
                        d={createSmoothWaferPath(waferTrajectory)}
                        fill="none"
                        stroke="orange"
                        strokeWidth="1.5"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="0.6"
                      />

                      {waferTrajectory.length > 0 && (
                        <>
                          <circle
                            cx={waferTrajectory[waferTrajectory.length - 1].x}
                            cy={-waferTrajectory[waferTrajectory.length - 1].y}
                            r="6"
                            fill="darkorange"
                            stroke="white"
                            strokeWidth="3"
                          />
                          <circle
                            cx={waferTrajectory[0].x}
                            cy={-waferTrajectory[0].y}
                            r="4"
                            fill="red"
                            stroke="white"
                            strokeWidth="2"
                          />
                        </>
                      )}
                    </g>
                  );
                })()}

                {/* 범례 */}
                <g transform="translate(120, -120)">
                  <rect x="0" y="0" width="120" height="60" fill="white" opacity="0.9" stroke="gray" strokeWidth="1" rx="3"/>
                  <text x="5" y="15" fontSize="10" fontWeight="bold" fill="black">패드→웨이퍼 자취</text>
                  <line x1="5" y1="25" x2="25" y2="25" stroke="darkorange" strokeWidth="3"/>
                  <text x="30" y="30" fontSize="9" fill="black">패드 200mm점</text>
                  <circle cx="15" cy="40" r="3" fill="red" stroke="white" strokeWidth="1"/>
                  <text x="25" y="45" fontSize="9" fill="black">시작점</text>
                  <circle cx="15" cy="50" r="4" fill="darkorange" stroke="white" strokeWidth="2"/>
                  <text x="25" y="55" fontSize="9" fill="black">현재위치</text>
                </g>
              </svg>
            </div>
            <p className="text-xs text-gray-600 mt-2">패드 위 200mm 지점이 고정된 웨이퍼 위에서 그리는 자취</p>
          </div>
        </div>

        <div className="mt-4 text-sm text-gray-600 bg-blue-50 p-3 rounded">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <p><strong>사용법:</strong></p>
              <p>• 재생/일시정지/리셋 버튼으로 애니메이션 제어</p>
              <p>• 시간 슬라이더로 특정 시점으로 이동 가능</p>
              <p>• 속도 조절로 세밀한 관찰 또는 빠른 개요 확인</p>
            </div>
            <div>
              <p><strong>그래프 설명:</strong></p>
              <p>• <strong>좌측:</strong> 대칭 웨이퍼의 추적점이 패드에 그리는 자취</p>
              <p>• <strong>중앙:</strong> 고정된 웨이퍼 위 300개 점의 누적 이동거리 히트맵</p>
              <p>• <strong>우측:</strong> 패드 위 200mm 지점이 웨이퍼에 그리는 자취</p>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // 누적 거리 분석
  const CumulativeDistanceAnalysis = () => {
    if (!results || selectedPoints.length === 0) return (
      <div className="w-full bg-white border rounded-lg p-4">
        <h3 className="text-lg font-semibold mb-4">선택된 점들의 누적 이동거리</h3>
        <p className="text-gray-600">점을 선택해주세요</p>
      </div>
    );

    const chartData = results.time_steps.map((time, i) => {
      const dataPoint = { time };
      selectedPoints.forEach(pointIndex => {
        if (i < results.trajectoryData[pointIndex].length) {
          dataPoint[`Point_${pointIndex}`] = results.trajectoryData[pointIndex][i].cumulative_distance;
        }
      });
      return dataPoint;
    });

    const colors = ['#8884d8', '#82ca9d', '#ffc658', '#ff7300', '#413ea0'];

    return (
      <div className="w-full bg-white border rounded-lg p-4">
        <h3 className="text-lg font-semibold mb-4">선택된 점들의 누적 이동거리</h3>
        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={chartData}>
            <CartesianGrid strokeDasharray="3,3" />
            <XAxis 
              dataKey="time" 
              label={{value: '시간 (s)', position: 'insideBottom', offset: -10}} 
            />
            <YAxis 
              label={{value: '누적 거리 (mm)', angle: -90, position: 'insideLeft'}} 
            />
            <Tooltip />
            <Legend />
            {selectedPoints.map((pointIndex, i) => (
              <Line 
                key={pointIndex}
                type="monotone" 
                dataKey={`Point_${pointIndex}`} 
                stroke={colors[i % colors.length]}
                strokeWidth={2}
                name={`점 ${pointIndex} (${results.wafer_points[pointIndex].zone})`}
              />
            ))}
          </LineChart>
        </ResponsiveContainer>
      </div>
    );
  };

  // 뷰 선택 탭
  const ViewTabs = () => (
    <div className="flex flex-wrap gap-2 mb-4">
      {[
        {id: 'trajectory', name: '실시간 애니메이션'},
        {id: 'cumulative', name: '누적 거리 분석'},
        {id: 'wafer3d', name: '3D 표면'}
      ].map(tab => (
        <button
          key={tab.id}
          onClick={() => setCurrentView(tab.id)}
          className={`px-3 py-2 rounded-md text-sm font-medium ${
            currentView === tab.id 
              ? 'bg-blue-500 text-white' 
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          {tab.name}
        </button>
      ))}
    </div>
  );

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-6">
      <h1 className="text-3xl font-bold text-center mb-8">고도화된 웨이퍼 폴리싱 분석 시뮬레이터</h1>

      <div className="space-y-4">
        <ControlPanel />

        <div>
          <ViewTabs />

          {isSimulating && (
            <div className="w-full h-96 bg-gray-100 border rounded-lg flex items-center justify-center">
              <div className="text-center">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
                <p className="text-gray-600">고도화된 시뮬레이션 실행 중...</p>
              </div>
            </div>
          )}

          {!isSimulating && results && (
            <div>
              {currentView === 'trajectory' && <AnimationView />}
              {currentView === 'cumulative' && <CumulativeDistanceAnalysis />}
              {currentView === 'wafer3d' && (
                <Surface3D 
                  data={results.finalDistances} 
                  points={results.wafer_points}
                  title="웨이퍼 3D 누적 이동거리" 
                  radius={results.R_wafer}
                />
              )}
            </div>
          )}
        </div>
      </div>

      {/* 선택된 점들 정보 */}
      {(selectedPoints.length > 0 || userSelectedPoints.length > 0) && (
        <div className="bg-white border rounded-lg p-4">
          <h3 className="text-lg font-semibold mb-2">선택된 점들 정보</h3>

          {selectedPoints.length > 0 && (
            <div className="mb-4">
              <h4 className="font-medium text-blue-600 mb-2">분석용 선택 점들 ({selectedPoints.length}/5)</h4>
              <div className="grid grid-cols-5 gap-4">
                {selectedPoints.map(pointIndex => {
                  const point = results?.wafer_points[pointIndex];
                  const finalDistance = results?.finalDistances[pointIndex];
                  return (
                    <div key={pointIndex} className="bg-blue-50 p-3 rounded">
                      <div className="font-medium">점 {pointIndex}</div>
                      <div className="text-sm">위치: ({point?.x.toFixed(1)}, {point?.y.toFixed(1)})</div>
                      <div className="text-sm">구역: {point?.zone}</div>
                      <div className="text-sm">반지름: {point?.r.toFixed(1)}mm</div>
                      <div className="text-sm">최종 거리: {finalDistance?.toFixed(2)}mm</div>
                      <button
                        onClick={() => handlePointSelection(pointIndex)}
                        className="mt-2 text-xs bg-red-500 text-white px-2 py-1 rounded"
                      >
                        제거
                      </button>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {userSelectedPoints.length > 0 && (
            <div>
              <h4 className="font-medium text-orange-600 mb-2">사용자 선택 점들 ({userSelectedPoints.length}/10)</h4>
              <div className="grid grid-cols-5 gap-4">
                {userSelectedPoints.map(pointIndex => {
                  const point = results?.wafer_points[pointIndex];
                  const finalDistance = results?.finalDistances[pointIndex];
                  return (
                    <div key={pointIndex} className="bg-orange-50 p-3 rounded">
                      <div className="font-medium">점 {pointIndex}</div>
                      <div className="text-sm">위치: ({point?.x.toFixed(1)}, {point?.y.toFixed(1)})</div>
                      <div className="text-sm">구역: {point?.zone}</div>
                      <div className="text-sm">반지름: {point?.r.toFixed(1)}mm</div>
                      <div className="text-sm">최종 거리: {finalDistance?.toFixed(2)}mm</div>
                      <button
                        onClick={() => {
                          setIsSelectingMode(true);
                          handlePointSelection(pointIndex);
                        }}
                        className="mt-2 text-xs bg-red-500 text-white px-2 py-1 rounded"
                      >
                        제거
                      </button>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {customPoints.length > 0 && (
            <div>
              <h4 className="font-medium text-purple-600 mb-2">사용자 정의 점들 ({customPoints.length}개)</h4>
              <div className="grid grid-cols-5 gap-4">
                {customPoints.map(point => {
                  const pointIndex = results?.wafer_points.findIndex(p => p.customId === point.id);
                  const finalDistance = pointIndex >= 0 ? results?.finalDistances[pointIndex] : 0;
                  const x = point.radius * Math.cos(point.angle * Math.PI / 180);
                  const y = point.radius * Math.sin(point.angle * Math.PI / 180);
                  return (
                    <div key={point.id} className="bg-purple-50 p-3 rounded">
                      <div className="font-medium">사용자 점 {point.id}</div>
                      <div className="text-sm">위치: ({x.toFixed(1)}, {y.toFixed(1)})</div>
                      <div className="text-sm">반지름: {point.radius}mm</div>
                      <div className="text-sm">각도: {point.angle}°</div>
                      <div className="text-sm">최종 거리: {finalDistance?.toFixed(2)}mm</div>
                      <button
                        onClick={() => removeCustomPoint(point.id)}
                        className="mt-2 text-xs bg-red-500 text-white px-2 py-1 rounded"
                      >
                        제거
                      </button>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};
