                {/* 하단 웨이퍼 */}
                <g transform={`translate(${currentData.wafer_center_x}, ${-currentData.wafer_center_y})`}>
                  <circle cx="0" cy="0" r={results.R_wafer} fill="none" stroke="darkblue" strokeWidth="2" opacity="0.4" />
                  <g transform={`rotate(${currentData.wafer_angle * 180 / Math.PI})`}>
                    <line x1="0" y1="0" x2={results.R_wafer * 0.8} y2="0" stroke="darkblue" strokeWidth="3" opacity="0.7"/>
                    <circle cx={results.R_wafer * 0.8} cy="0" r="4" fill="darkblue" opacity="0.9"/>
                  </g>
                </g>
                <text x={currentData.wafer_center_x} y={-currentData.wafer_center_y + results.R_wafer + 25} textAnchor="middle" fontSize="10" fill="darkblue">웨이퍼2</text>

                {/* 기본 추적 점들의 자취선 (150mm 점만, 사용자 정의에서 선택되지 않은 경우) */}
                {(() => {
                  const has150mmCustom = customPoints.some(p => p.radius === 150 && p.angle === 0);
                  if (has150mmCustom) return null;

                  return defaultTrackingPoints.map(point => {
                    const pointIndex = results.wafer_points.findIndex(p => p.customId === point.id);
                    if (pointIndex === -1) return null;

                  const startIndex = Math.max(0, currentTimeIndex - trailLength + 1);
                  const trajectorySegments = results.padTrajectoryData[pointIndex]
                    .slice(startIndex, currentTimeIndex + 1)
                    .filter(trajPoint => {
                      return Math.sqrt(trajPoint.x * trajPoint.x + trajPoint.y * trajPoint.y) <= results.R_pad;
                    });

                  if (trajectorySegments.length < 2) return null;

                  return (
                    <g key={`default-trail-${point.id}`}>
                      <path
                        d={createSmoothPath(trajectorySegments, true)}
                        fill="none"
                        stroke="blue"
                        strokeWidth="2"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="0.8"
                      />
                      <path
                        d={createSmoothPath(trajectorySegments, false)}
                        fill="none"
                        stroke="blue"
                        strokeWidth="2"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="0.8"
                        strokeDasharray="3,3"
                      />

                      {/* 하단 웨이퍼 궤적 (실제 계산된 데이터 사용) */}
                      {(() => {
                        const lowerTrajectorySegments = results.padTrajectoryDataLower[pointIndex]
                          .slice(startIndex, currentTimeIndex + 1)
                          .filter(trajPoint => {
                            return Math.sqrt(trajPoint.x * trajPoint.x + trajPoint.y * trajPoint.y) <= results.R_pad;
                          });

                        if (lowerTrajectorySegments.length < 2) return null;

                        return (
                          <path
                            d={createSmoothPath(lowerTrajectorySegments, false)}
                            fill="none"
                            stroke="darkblue"
                            strokeWidth="2"
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            opacity="0.8"
                            strokeDasharray="5,5"
                          />
                        );
                      })()}
                      {currentTimeIndex < trajectorySegments.length && (
                        <>
                          <circle
                            cx={trajectorySegments[trajectorySegments.length - 1].x}
                            cy={-trajectorySegments[trajectorySegments.length - 1].y}
                            r="4"
                            fill="blue"
                            stroke="white"
                            strokeWidth="2"
                          />
                          <circle
                            cx={trajectorySegments[trajectorySegments.length - 1].x}
                            cy={trajectorySegments[trajectorySegments.length - 1].y}
                            r="4"
                            fill="blue"
                            stroke="white"
                            strokeWidth="2"
                          />

                          {/* 하단 웨이퍼 현재 위치 */}
                          {(() => {
                            const lowerTrajectorySegments = results.padTrajectoryDataLower[pointIndex]
                              .slice(startIndex, currentTimeIndex + 1)
                              .filter(trajPoint => {
                                return Math.sqrt(trajPoint.x * trajPoint.x + trajPoint.y * trajPoint.y) <= results.R_pad;
                              });

                            if (lowerTrajectorySegments.length === 0) return null;

                            return (
                              <circle
                                cx={lowerTrajectorySegments[lowerTrajectorySegments.length - 1].x}
                                cy={lowerTrajectorySegments[lowerTrajectorySegments.length - 1].y}
                                r="4"
                                fill="darkblue"
                                stroke="white"
                                strokeWidth="2"
                              />
                            );
                          })()}
                        </>
                      )}
                    </g>
                  );
                  });
                })()}

                {/* 사용자 정의 점들의 자취선 */}
                {customPoints.map(point => {
                  const pointIndex = results.wafer_points.findIndex(p => p.customId === point.id);
                  if (pointIndex === -1) return null;

                  const startIndex = Math.max(0, currentTimeIndex - trailLength + 1);
                  const trajectorySegments = results.padTrajectoryData[pointIndex]
                    .slice(startIndex, currentTimeIndex + 1)
                    .filter(trajPoint => {
                      return Math.sqrt(trajPoint.x * trajPoint.x + trajPoint.y * trajPoint.y) <= results.R_pad;
                    });

                  if (trajectorySegments.length < 2) return null;

                  return (
                    <g key={`custom-trail-${point.id}`}>
                      <path
                        d={createSmoothPath(trajectorySegments, true)}
                        fill="none"
                        stroke="purple"
                        strokeWidth="3"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="1"
                      />
                      <path
                        d={createSmoothPath(trajectorySegments, false)}
                        fill="none"
                        stroke="purple"
                        strokeWidth="3"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="1"
                        strokeDasharray="5,5"
                      />

                      {/* 하단 웨이퍼 궤적 (사용자 정의 점) */}
                      {(() => {
                        const lowerTrajectorySegments = results.padTrajectoryDataLower[pointIndex]
                          .slice(startIndex, currentTimeIndex + 1)
                          .filter(trajPoint => {
                            return Math.sqrt(trajPoint.x * trajPoint.x + trajPoint.y * trajPoint.y) <= results.R_pad;
                          });

                        if (lowerTrajectorySegments.length < 2) return null;

                        return (
                          <path
                            d={createSmoothPath(lowerTrajectorySegments, false)}
                            fill="none"
                            stroke="darkmagenta"
                            strokeWidth="3"
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            opacity="1"
                            strokeDasharray="7,7"
                          />
                        );
                      })()}
                      {currentTimeIndex < trajectorySegments.length && (
                        <>
                          <circle
                            cx={trajectorySegments[trajectorySegments.length - 1].x}
                            cy={-trajectorySegments[trajectorySegments.length - 1].y}
                            r="6"
                            fill="purple"
                            stroke="darkmagenta"
                            strokeWidth="3"
                          />
                          <circle
                            cx={trajectorySegments[trajectorySegments.length - 1].x}
                            cy={trajectorySegments[trajectorySegments.length - 1].y}
                            r="6"
                            fill="purple"
                            stroke="darkmagenta"
                            strokeWidth="3"
                          />

                          {/* 하단 웨이퍼 현재 위치 (사용자 정의 점) */}
                          {(() => {
                            const lowerTrajectorySegments = results.padTrajectoryDataLower[pointIndex]
                              .slice(startIndex, currentTimeIndex + 1)
                              .filter(trajPoint => {
                                return Math.sqrt(trajPoint.x * trajPoint.x + trajPoint.y * trajPoint.y) <= results.R_pad;
                              });

                            if (lowerTrajectorySegments.length === 0) return null;

                            return (
                              <circle
                                cx={lowerTrajectorySegments[lowerTrajectorySegments.length - 1].x}
                                cy={lowerTrajectorySegments[lowerTrajectorySegments.length - 1].y}
                                r="6"
                                fill="darkmagenta"
                                stroke="white"
