                {/* 히트맵 점들 */}
                {results.heatmap_wafer_points.map((point, i) => {
                  const currentDistance = currentTimeIndex < results.heatmap_trajectoryData[i].length ? 
                    results.heatmap_trajectoryData[i][currentTimeIndex].cumulative_distance : 0;

                  const allCurrentDistances = results.heatmap_wafer_points.map((_, idx) => 
                    currentTimeIndex < results.heatmap_trajectoryData[idx].length ? 
                    results.heatmap_trajectoryData[idx][currentTimeIndex].cumulative_distance : 0
                  );
                  const maxCurrentDistance = Math.max(...allCurrentDistances);
                  const minCurrentDistance = Math.min(...allCurrentDistances);
                  const range = maxCurrentDistance - minCurrentDistance;

                  const intensity = range > 0 ? (currentDistance - minCurrentDistance) / range : 0;
                  const pointSize = Math.max(2, 2 + intensity * 6);
                  const hue = (1 - intensity) * 240;
                  const saturation = 80 + intensity * 20;
                  const lightness = 30 + intensity * 40;

                  return (
                    <circle
                      key={`heatmap-${i}`}
                      cx={point.x}
                      cy={-point.y}
                      r={pointSize}
                      fill={`hsl(${hue}, ${saturation}%, ${lightness}%)`}
                      opacity="0.8"
                      stroke={intensity > 0.8 ? 'white' : 'none'}
                      strokeWidth="0.5"
                    />
                  );
                })}

