                    return (
                      <>
                        <text x="5" y="45" fontSize="8" fill="black">{minDist.toFixed(1)}</text>
                        <text x="45" y="45" fontSize="8" fill="black" textAnchor="middle">{((minDist + maxDist) / 2).toFixed(1)}</text>
                        <text x="85" y="45" fontSize="8" fill="black" textAnchor="end">{maxDist.toFixed(1)}</text>
                        <text x="5" y="70" fontSize="8" fill="gray">시간: {currentData.time.toFixed(1)}s</text>
                      </>
                    );
                  })()}
                </g>
              </svg>
            </div>
            <p className="text-xs text-gray-600 mt-2">고정된 웨이퍼 위 300개 점의 누적 이동거리 히트맵</p>
          </div>

          {/* 3. 웨이퍼 좌표계 뷰 - 패드 200mm 자취 */}
          <div className="bg-white border rounded-lg p-4">
            <h4 className="text-lg font-semibold mb-4">웨이퍼 좌표계 (패드 200mm점 자취)</h4>
            <div className="relative w-full h-80 bg-gray-50 border">
              <svg width="100%" height="100%" viewBox={`${-results.R_wafer*1.2} ${-results.R_wafer*1.2} ${results.R_wafer*2.4} ${results.R_wafer*2.4}`}>
                <circle cx="0" cy="0" r={results.R_wafer} fill="none" stroke="blue" strokeWidth="3" />
                <line x1="0" y1="0" x2={results.R_wafer * 0.8} y2="0" stroke="blue" strokeWidth="3" opacity="0.6"/>
                <circle cx={results.R_wafer * 0.8} cy="0" r="4" fill="blue" opacity="0.8"/>
                <text x="0" y={results.R_wafer + 30} textAnchor="middle" fontSize="12" fill="blue">웨이퍼 (고정)</text>

                {/* 패드 200mm 지점 자취 */}
                {(() => {
                  const padPoint = { x: 200, y: 0 };

                  // 더 조밀한 시간 간격으로 궤적 생성
                  const waferTrajectory = [];
                  const interpolation_factor = 5; // 5배 더 조밀하게

                  for (let i = 0; i <= currentTimeIndex; i++) {
                    const data = results.timeSeriesData[i];

                    // 패드 회전에 따른 전역 좌표 변환
                    const cos_p = Math.cos(data.pad_angle);
                    const sin_p = Math.sin(data.pad_angle);
                    const global_x = padPoint.x * cos_p - padPoint.y * sin_p;
                    const global_y = padPoint.x * sin_p + padPoint.y * cos_p;

                    // 웨이퍼 중심 기준 상대 좌표 (진동 효과 포함)
                    const rel_x = global_x - data.wafer_center_x;
                    const rel_y = global_y - data.wafer_center_y;

                    // 웨이퍼 회전에 따른 웨이퍼 좌표계 변환
                    const cos_w = Math.cos(-data.wafer_angle);
                    const sin_w = Math.sin(-data.wafer_angle);
                    const wafer_x = rel_x * cos_w - rel_y * sin_w;
                    const wafer_y = rel_x * sin_w + rel_y * cos_w;

                    waferTrajectory.push({ x: wafer_x, y: wafer_y, time: data.time });

                    // 다음 시간 스텝과의 중간점들 추가 (보간)
                    if (i < currentTimeIndex && i < results.timeSeriesData.length - 1) {
                      const nextData = results.timeSeriesData[i + 1];
                      for (let j = 1; j < interpolation_factor; j++) {
                        const t = j / interpolation_factor;

                        // 보간된 각도와 위치 계산
                        const interpolatedPadAngle = data.pad_angle + (nextData.pad_angle - data.pad_angle) * t;
                        const interpolatedWaferAngle = data.wafer_angle + (nextData.wafer_angle - data.wafer_angle) * t;
                        const interpolatedWaferCenterX = data.wafer_center_x + (nextData.wafer_center_x - data.wafer_center_x) * t;
                        const interpolatedWaferCenterY = data.wafer_center_y + (nextData.wafer_center_y - data.wafer_center_y) * t;

                        // 보간된 패드 회전에 따른 전역 좌표 변환
                        const cos_p_interp = Math.cos(interpolatedPadAngle);
                        const sin_p_interp = Math.sin(interpolatedPadAngle);
                        const global_x_interp = padPoint.x * cos_p_interp - padPoint.y * sin_p_interp;
                        const global_y_interp = padPoint.x * sin_p_interp + padPoint.y * cos_p_interp;

                        // 보간된 웨이퍼 중심 기준 상대 좌표 (진동 효과 포함)
                        const rel_x_interp = global_x_interp - interpolatedWaferCenterX;
                        const rel_y_interp = global_y_interp - interpolatedWaferCenterY;

                        // 보간된 웨이퍼 회전에 따른 웨이퍼 좌표계 변환
                        const cos_w_interp = Math.cos(-interpolatedWaferAngle);
                        const sin_w_interp = Math.sin(-interpolatedWaferAngle);
                        const wafer_x_interp = rel_x_interp * cos_w_interp - rel_y_interp * sin_w_interp;
                        const wafer_y_interp = rel_x_interp * sin_w_interp + rel_y_interp * cos_w_interp;

                        waferTrajectory.push({ 
                          x: wafer_x_interp, 
                          y: wafer_y_interp, 
                          time: data.time + (nextData.time - data.time) * t 
                        });
                      }
                    }
                  }

                  // 웨이퍼 내부에 있는 점들만 필터링
                  const filteredTrajectory = waferTrajectory.filter(point => 
                    Math.sqrt(point.x * point.x + point.y * point.y) <= results.R_wafer
                  );

                  if (filteredTrajectory.length < 2) return null;

                  // 고도화된 부드러운 곡선 생성 함수
                  const createSmoothWaferPath = (points) => {
                    if (points.length < 2) return '';

                    // B-스플라인 스무딩을 위한 추가 보간
                    const smoothedPoints = [];
                    for (let i = 0; i < points.length - 1; i++) {
                      const current = points[i];
                      const next = points[i + 1];

                      smoothedPoints.push(current);

                      // 중간에 2개의 보간점 추가
                      for (let j = 1; j <= 2; j++) {
                        const t = j / 3;
                        const interpolated = {
                          x: current.x + (next.x - current.x) * t,
                          y: current.y + (next.y - current.y) * t
                        };
                        smoothedPoints.push(interpolated);
                      }
                    }
                    smoothedPoints.push(points[points.length - 1]);

                    // 3차 베지어 곡선으로 부드러운 경로 생성
                    let path = `M ${smoothedPoints[0].x} ${-smoothedPoints[0].y}`;

                    for (let i = 1; i < smoothedPoints.length; i++) {
                      const prev = smoothedPoints[i - 1];
                      const curr = smoothedPoints[i];

                      if (i === 1) {
                        // 첫 번째 세그먼트
                        path += ` Q ${prev.x} ${-prev.y} ${curr.x} ${-curr.y}`;
                      } else {
                        // 후속 세그먼트는 3차 베지어
                        const prev2 = smoothedPoints[i - 2];
                        const cp1x = prev.x + (curr.x - prev2.x) * 0.3;
                        const cp1y = prev.y + (curr.y - prev2.y) * 0.3;
                        const cp2x = curr.x - (curr.x - prev.x) * 0.3;
                        const cp2y = curr.y - (curr.y - prev.y) * 0.3;

                        path += ` C ${cp1x} ${-cp1y} ${cp2x} ${-cp2y} ${curr.x} ${-curr.y}`;
                      }
                    }

                    return path;
                  };

                  return (
                    <g key="pad-200mm-trail">
                      <path
                        d={createSmoothWaferPath(filteredTrajectory)}
                        fill="none"
                        stroke="darkorange"
                        strokeWidth="3"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="0.8"
                      />
                      <path
                        d={createSmoothWaferPath(filteredTrajectory)}
                        fill="none"
                        stroke="orange"
                        strokeWidth="1.5"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        opacity="0.6"
                      />

                      {filteredTrajectory.length > 0 && (
                        <>
                          <circle
                            cx={filteredTrajectory[filteredTrajectory.length - 1].x}
                            cy={-filteredTrajectory[filteredTrajectory.length - 1].y}
                            r="6"
                            fill="darkorange"
                            stroke="white"
                            strokeWidth="3"
                          />
                          <circle
                            cx={filteredTrajectory[0].x}
                            cy={-filteredTrajectory[0].y}
                            r="4"
                            fill="red"
                            stroke="white"
                            strokeWidth="2"
                          />
                        </>
                      )}
                    </g>
                  );
                })()}

                {/* 범례 */}
                <g transform="translate(120, -120)">
                  <rect x="0" y="0" width="120" height="60" fill="white" opacity="0.9" stroke="gray" strokeWidth="1" rx="3"/>
                  <text x="5" y="15" fontSize="10" fontWeight="bold" fill="black">패드→웨이퍼 자취</text>
                  <line x1="5" y1="25" x2="25" y2="25" stroke="darkorange" strokeWidth="3"/>
                  <text x="30" y="30" fontSize="9" fill="black">패드 200mm점</text>
                  <circle cx="15" cy="40" r="3" fill="red" stroke="white" strokeWidth="1"/>
                  <text x="25" y="45" fontSize="9" fill="black">시작점</text>
                  <circle cx="15" cy="50" r="4" fill="darkorange" stroke="white" strokeWidth="2"/>
                  <text x="25" y="55" fontSize="9" fill="black">현재위치</text>
                </g>
              </svg>
            </div>
            <p className="text-xs text-gray-600 mt-2">
              패드 위 200mm 지점이 고정된 웨이퍼 위에서 그리는 자취<br/>
              (패드 회전 + 웨이퍼 회전 + 웨이퍼 진동 효과 반영)
            </p>
          </div>
        </div>

        <div className="mt-4 text-sm text-gray-600 bg-blue-50 p-3 rounded">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <p><strong>사용법:</strong></p>
              <p>• 재생/일시정지/리셋 버튼으로 애니메이션 제어</p>
              <p>• 시간 슬라이더로 특정 시점으로 이동 가능</p>
              <p>• 속도 조절로 세밀한 관찰 또는 빠른 개요 확인</p>
            </div>
            <div>
              <p><strong>그래프 설명:</strong></p>
              <p>• <strong>좌측:</strong> 대칭 웨이퍼의 추적점이 패드에 그리는 자취</p>
              <p>• <strong>중앙:</strong> 고정된 웨이퍼 위 300개 점의 누적 이동거리 히트맵</p>
              <p>• <strong>우측:</strong> 패드 위 200mm 지점이 웨이퍼에 그리는 자취</p>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // 누적 거리 분석
  const CumulativeDistanceAnalysis = () => {
    if (!results || selectedPoints.length === 0) return (
      <div className="w-full bg-white border rounded-lg p-4">
        <h3 className="text-lg font-semibold mb-4">선택된 점들의 누적 이동거리</h3>
        <p className="text-gray-600">점을 선택해주세요</p>
      </div>
    );

    const chartData = results.time_steps.map((time, i) => {
      const dataPoint = { time };
      selectedPoints.forEach(pointIndex => {
        if (i < results.trajectoryData[pointIndex].length) {
          dataPoint[`Point_${pointIndex}`] = results.trajectoryData[pointIndex][i].cumulative_distance;
        }
      });
      return dataPoint;
    });

    const colors = ['#8884d8', '#82ca9d', '#ffc658', '#ff7300', '#413ea0'];

    return (
      <div className="w-full bg-white border rounded-lg p-4">
        <h3 className="text-lg font-semibold mb-4">선택된 점들의 누적 이동거리</h3>
        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={chartData}>
            <CartesianGrid strokeDasharray="3,3" />
            <XAxis 
              dataKey="time" 
              label={{value: '시간 (s)', position: 'insideBottom', offset: -10}} 
            />
            <YAxis 
              label={{value: '누적 거리 (mm)', angle: -90, position: 'insideLeft'}} 
            />
            <Tooltip />
            <Legend />
            {selectedPoints.map((pointIndex, i) => (
              <Line 
                key={pointIndex}
                type="monotone" 
                dataKey={`Point_${pointIndex}`} 
                stroke={colors[i % colors.length]}
                strokeWidth={2}
                name={`점 ${pointIndex} (${results.wafer_points[pointIndex].zone})`}
              />
            ))}
          </LineChart>
        </ResponsiveContainer>
      </div>
    );
  };

  // 뷰 선택 탭
  const ViewTabs = () => (
    <div className="flex flex-wrap gap-2 mb-4">
      {[
        {id: 'trajectory', name: '실시간 애니메이션'},
        {id: 'cumulative', name: '누적 거리 분석'}
      ].map(tab => (
        <button
          key={tab.id}
          onClick={() => setCurrentView(tab.id)}
          className={`px-3 py-2 rounded-md text-sm font-medium ${
            currentView === tab.id 
              ? 'bg-blue-500 text-white' 
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          {tab.name}
        </button>
      ))}
    </div>
  );

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-6">
      <h1 className="text-3xl font-bold text-center mb-8">고도화된 웨이퍼 폴리싱 분석 시뮬레이터</h1>

      <div className="space-y-4">
        <ControlPanel />

        <div>
          <ViewTabs />

          {isSimulating && (
            <div className="w-full h-96 bg-gray-100 border rounded-lg flex items-center justify-center">
              <div className="text-center">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
                <p className="text-gray-600">고도화된 시뮬레이션 실행 중...</p>
              </div>
            </div>
          )}

          {!isSimulating && results && (
            <div>
              {currentView === 'trajectory' && <AnimationView />}
              {currentView === 'cumulative' && <CumulativeDistanceAnalysis />}
            </div>
          )}
        </div>
      </div>

      {/* 선택된 점들 정보 */}
      {(selectedPoints.length > 0 || userSelectedPoints.length > 0) && (
        <div className="bg-white border rounded-lg p-4">
          <h3 className="text-lg font-semibold mb-2">선택된 점들 정보</h3>

          {selectedPoints.length > 0 && (
            <div className="mb-4">
              <h4 className="font-medium text-blue-600 mb-2">분석용 선택 점들 ({selectedPoints.length}/5)</h4>
              <div className="grid grid-cols-5 gap-4">
                {selectedPoints.map(pointIndex => {
                  const point = results?.wafer_points[pointIndex];
                  const finalDistance = results?.finalDistances[pointIndex];
                  return (
                    <div key={pointIndex} className="bg-blue-50 p-3 rounded">
                      <div className="font-medium">점 {pointIndex}</div>
                      <div className="text-sm">위치: ({point?.x.toFixed(1)}, {point?.y.toFixed(1)})</div>
                      <div className="text-sm">구역: {point?.zone}</div>
                      <div className="text-sm">반지름: {point?.r.toFixed(1)}mm</div>
                      <div className="text-sm">최종 거리: {finalDistance?.toFixed(2)}mm</div>
                      <button
                        onClick={() => handlePointSelection(pointIndex)}
                        className="mt-2 text-xs bg-red-500 text-white px-2 py-1 rounded"
                      >
                        제거
                      </button>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {userSelectedPoints.length > 0 && (
            <div>
              <h4 className="font-medium text-orange-600 mb-2">사용자 선택 점들 ({userSelectedPoints.length}/10)</h4>
              <div className="grid grid-cols-5 gap-4">
                {userSelectedPoints.map(pointIndex => {
                  const point = results?.wafer_points[pointIndex];
                  const finalDistance = results?.finalDistances[pointIndex];
                  return (
                    <div key={pointIndex} className="bg-orange-50 p-3 rounded">
                      <div className="font-medium">점 {pointIndex}</div>
                      <div className="text-sm">위치: ({point?.x.toFixed(1)}, {point?.y.toFixed(1)})</div>
                      <div className="text-sm">구역: {point?.zone}</div>
                      <div className="text-sm">반지름: {point?.r.toFixed(1)}mm</div>
                      <div className="text-sm">최종 거리: {finalDistance?.toFixed(2)}mm</div>
                      <button
                        onClick={() => {
                          setIsSelectingMode(true);
                          handlePointSelection(pointIndex);
                        }}
                        className="mt-2 text-xs bg-red-500 text-white px-2 py-1 rounded"
                      >
                        제거
                      </button>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {customPoints.length > 0 && (
            <div>
              <h4 className="font-medium text-purple-600 mb-2">사용자 정의 점들 ({customPoints.length}개)</h4>
              <div className="grid grid-cols-5 gap-4">
                {customPoints.map(point => {
                  const pointIndex = results?.wafer_points.findIndex(p => p.customId === point.id);
                  const finalDistance = pointIndex >= 0 ? results?.finalDistances[pointIndex] : 0;
                  const x = point.radius * Math.cos(point.angle * Math.PI / 180);
                  const y = point.radius * Math.sin(point.angle * Math.PI / 180);
                  return (
                    <div key={point.id} className="bg-purple-50 p-3 rounded">
                      <div className="font-medium">사용자 점 {point.id}</div>
                      <div className="text-sm">위치: ({x.toFixed(1)}, {y.toFixed(1)})</div>
                      <div className="text-sm">반지름: {point.radius}mm</div>
                      <div className="text-sm">각도: {point.angle}°</div>
                      <div className="text-sm">최종 거리: {finalDistance?.toFixed(2)}mm</div>
                      <button
                        onClick={() => removeCustomPoint(point.id)}
                        className="mt-2 text-xs bg-red-500 text-white px-2 py-1 rounded"
                      >
                        제거
                      </button>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default AdvancedWaferAnalysisSimulator;
