import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const CorrectedWaferAnalysisSimulator = () => {
  // 시뮬레이션 파라미터
  const [params, setParams] = useState({
    R_wafer: 150,
    R_pad: 400,
    rpm_pad: 31,
    rpm_wafer: 30,
    R_c: 225,
    time_total: 150,
    num_time_steps: 1500,
    oscillation_enabled: true,
    oscillation_amplitude: 40,
    oscillation_freq_hz: 0.5, // 진동 주파수를 Hz로 명확히 정의
    num_wafer_grid: 400,
    heatmap_grid_points: 300
  });

  const [currentView, setCurrentView] = useState('trajectory');
  const [isSimulating, setIsSimulating] = useState(false);
  const [results, setResults] = useState(null);
  const [selectedPoints, setSelectedPoints] = useState([]);
  const [userSelectedPoints, setUserSelectedPoints] = useState([]);
  const [customPoints, setCustomPoints] = useState([]);
  const [newPointRadius, setNewPointRadius] = useState(75);
  const [newPointAngle, setNewPointAngle] = useState(0);
  const [isSelectingMode, setIsSelectingMode] = useState(false);
  const [animationTime, setAnimationTime] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [animationSpeed, setAnimationSpeed] = useState(1.0);
  const [trailLength, setTrailLength] = useState(100);

  // 기본 추적 점들
  const defaultTrackingPoints = [
    { id: 'r150', radius: 150, angle: 0, label: '150mm' }
  ];

  const animationRef = useRef();
  const isPlayingRef = useRef(false);
  const lastFrameTimeRef = useRef(0);

  const animate = useCallback((currentTime) => {
    if (!isPlayingRef.current) return;

    if (lastFrameTimeRef.current === 0) {
      lastFrameTimeRef.current = currentTime;
    }

    const deltaTime = currentTime - lastFrameTimeRef.current;
    lastFrameTimeRef.current = currentTime;

    const simulationDt = results?.timeSeriesData?.[1]?.time - results?.timeSeriesData?.[0]?.time || 0.1;
    const realTimeSeconds = deltaTime / 1000;
    const simulationTimeIncrement = realTimeSeconds * animationSpeed;

    setAnimationTime(prev => {
      const next = prev + simulationTimeIncrement;
      const maxTime = results?.timeSeriesData?.length - 1 || 0;

      if (next >= maxTime) {
        isPlayingRef.current = false;
        setIsPlaying(false);
        if (animationRef.current) {
          cancelAnimationFrame(animationRef.current);
          animationRef.current = null;
        }
        lastFrameTimeRef.current = 0;
        return maxTime;
      }

      if (isPlayingRef.current) {
        animationRef.current = requestAnimationFrame(animate);
      }
      return next;
    });
  }, [animationSpeed, results]);

  // 수정된 시뮬레이션 계산
  const runSimulation = useMemo(() => {
    return () => {
      setIsSimulating(true);

      setTimeout(() => {
        const {
          R_wafer, R_pad, rpm_pad, rpm_wafer, R_c, time_total, 
          num_time_steps, oscillation_enabled, oscillation_amplitude, 
          oscillation_freq_hz, num_wafer_grid
        } = params;

        // 각속도 계산 (rpm → rad/s)
        const omega_pad = (rpm_pad * 2 * Math.PI) / 60;
        const omega_wafer = (rpm_wafer * 2 * Math.PI) / 60;

        // 시간 스텝
        const time_steps = Array.from({length: num_time_steps}, (_, i) => 
          (i * time_total) / (num_time_steps - 1)
        );
        const dt = time_steps[1] - time_steps[0];

        // 수정된 진동 계산 (물리적으로 올바른 주파수 정의)
        let wafer_center_x, wafer_center_y, wafer_center_vx, wafer_center_vy;

        if (oscillation_enabled) {
          // 웨이퍼 중심의 운동 (극좌표에서 진동)
          wafer_center_x = time_steps.map(t => {
            const r_oscillating = R_c + oscillation_amplitude * Math.sin(2 * Math.PI * oscillation_freq_hz * t);
            return r_oscillating * Math.cos(omega_pad * t);
          });

          wafer_center_y = time_steps.map(t => {
            const r_oscillating = R_c + oscillation_amplitude * Math.sin(2 * Math.PI * oscillation_freq_hz * t);
            return r_oscillating * Math.sin(omega_pad * t);
          });

          // 웨이퍼 중심의 속도 (정확한 미분)
          wafer_center_vx = time_steps.map(t => {
            const r_osc = R_c + oscillation_amplitude * Math.sin(2 * Math.PI * oscillation_freq_hz * t);
            const r_dot = oscillation_amplitude * 2 * Math.PI * oscillation_freq_hz * Math.cos(2 * Math.PI * oscillation_freq_hz * t);

            return r_dot * Math.cos(omega_pad * t) - r_osc * omega_pad * Math.sin(omega_pad * t);
          });

          wafer_center_vy = time_steps.map(t => {
            const r_osc = R_c + oscillation_amplitude * Math.sin(2 * Math.PI * oscillation_freq_hz * t);
            const r_dot = oscillation_amplitude * 2 * Math.PI * oscillation_freq_hz * Math.cos(2 * Math.PI * oscillation_freq_hz * t);

            return r_dot * Math.sin(omega_pad * t) + r_osc * omega_pad * Math.cos(omega_pad * t);
          });
        } else {
          // 진동 없는 경우
          wafer_center_x = time_steps.map(t => R_c * Math.cos(omega_pad * t));
          wafer_center_y = time_steps.map(t => R_c * Math.sin(omega_pad * t));
          wafer_center_vx = time_steps.map(t => -R_c * omega_pad * Math.sin(omega_pad * t));
          wafer_center_vy = time_steps.map(t => R_c * omega_pad * Math.cos(omega_pad * t));
        }

        // 웨이퍼 격자 생성
        const wafer_points = [];
        const radial_samples = Math.ceil(Math.sqrt(num_wafer_grid / 4));
        const angular_samples = Math.ceil(num_wafer_grid / radial_samples);

        wafer_points.push({x: 0, y: 0, r: 0, theta: 0, zone: 'center'});

        for (let i = 1; i <= radial_samples; i++) {
          const r = (i / radial_samples) * R_wafer;
          const num_theta = Math.max(6, Math.floor(angular_samples * i / radial_samples));

          for (let j = 0; j < num_theta; j++) {
            const theta = (2 * Math.PI * j) / num_theta;
            const x = r * Math.cos(theta);
            const y = r * Math.sin(theta);

            let zone = 'outer';
            if (r < R_wafer * 0.3) zone = 'inner';
            else if (r < R_wafer * 0.7) zone = 'middle';

            wafer_points.push({x, y, r, theta, zone});
          }
        }

        // 히트맵용 웨이퍼 점들
        const heatmap_wafer_points = [];
        const target_points = 300;
        const grid_size = Math.ceil(Math.sqrt(target_points * 4 / Math.PI));
        const cell_size = (2 * R_wafer) / grid_size;

        for (let i = 0; i < grid_size; i++) {
          for (let j = 0; j < grid_size; j++) {
            const x = (i + 0.5) * cell_size - R_wafer;
            const y = (j + 0.5) * cell_size - R_wafer;

            const distance_from_center = Math.sqrt(x * x + y * y);
            if (distance_from_center <= R_wafer) {
              const r = distance_from_center;
              const theta = Math.atan2(y, x);

              let zone = 'outer';
              if (r < R_wafer * 0.3) zone = 'inner';
              else if (r < R_wafer * 0.7) zone = 'middle';

              heatmap_wafer_points.push({x, y, r, theta, zone});
            }
          }
        }

        if (heatmap_wafer_points.length > target_points) {
          const shuffled = [...heatmap_wafer_points];
          for (let i = shuffled.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
          }
          heatmap_wafer_points.length = 0;
          heatmap_wafer_points.push(...shuffled.slice(0, target_points));
        }

        // 추적 점들 추가
        const has150mmCustom = customPoints.some(p => p.radius === 150 && p.angle === 0);
        const filteredDefaultPoints = has150mmCustom ? [] : defaultTrackingPoints;
        const allTrackingPoints = [...filteredDefaultPoints, ...customPoints];

        allTrackingPoints.forEach(point => {
          const x = point.radius * Math.cos(point.angle * Math.PI / 180);
          const y = point.radius * Math.sin(point.angle * Math.PI / 180);
          if (Math.sqrt(x * x + y * y) <= R_wafer) {
            wafer_points.push({
              x: x, 
              y: y, 
              r: point.radius, 
              theta: point.angle * Math.PI / 180, 
              zone: point.id ? 'default' : 'custom',
              isCustom: !point.id,
              isDefault: !!point.id,
              customId: point.id,
              label: point.label || `사용자점 ${point.id}`
            });
          }
        });

        const num_actual_grid = wafer_points.length;
        const wafer_area = Math.PI * R_wafer * R_wafer;
        const cell_area = wafer_area / num_actual_grid;

        // 데이터 저장소
        const heatmap_trajectoryData = heatmap_wafer_points.map(() => []);
        const heatmap_cumulativeDistance = heatmap_wafer_points.map(() => 0);
        const timeSeriesData = [];
        const trajectoryData = wafer_points.map(() => []);
        const cumulativeDistance = wafer_points.map(() => 0);
        const padTrajectoryData = wafer_points.map(() => []);
        const padTrajectoryDataLower = wafer_points.map(() => []);

        // 시뮬레이션 메인 루프 (수정된 물리 계산)
        for (let k = 0; k < num_time_steps; k++) {
          const t = time_steps[k];
          const wafer_theta = omega_wafer * t;

          const wafer_xc = wafer_center_x[k];
          const wafer_yc = wafer_center_y[k];
          const wafer_vxc = wafer_center_vx[k];
          const wafer_vyc = wafer_center_vy[k];

          // 웨이퍼 회전 변환 행렬
          const cos_w = Math.cos(wafer_theta);
          const sin_w = Math.sin(wafer_theta);

          const timeStepData = {
            time: t,
            wafer_center_x: wafer_xc,
            wafer_center_y: wafer_yc,
            wafer_angle: wafer_theta,
            pad_angle: omega_pad * t,
            points: []
          };

          // 일반 웨이퍼 점들 계산 (수정된 물리 모델)
          for (let i = 0; i < num_actual_grid; i++) {
            const {x: wx, y: wy, zone} = wafer_points[i];

            // 1. 웨이퍼 좌표계에서 Global 좌표계로 변환
            const abs_x = wafer_xc + (wx * cos_w - wy * sin_w);
            const abs_y = wafer_yc + (wx * sin_w + wy * cos_w);

            // 2. 수정된 속도 계산 (정확한 물리 모델)
            // 웨이퍼 회전에 의한 속도 (웨이퍼 좌표계)
            const v_rot_local_x = -omega_wafer * wy;  // ω × r의 x성분
            const v_rot_local_y = omega_wafer * wx;   // ω × r의 y성분

            // 웨이퍼 회전 속도를 Global 좌표계로 변환
            const v_rot_global_x = v_rot_local_x * cos_w - v_rot_local_y * sin_w;
            const v_rot_global_y = v_rot_local_x * sin_w + v_rot_local_y * cos_w;

            // 웨이퍼 점의 전체 Global 속도 (웨이퍼 중심 이동 + 회전)
            const v_global_x = wafer_vxc + v_rot_global_x;
            const v_global_y = wafer_vyc + v_rot_global_y;

            // 3. 패드 상의 해당 점의 속도 (패드 회전에 의한)
            const v_pad_x = -omega_pad * abs_y;  // 패드 회전 속도
            const v_pad_y = omega_pad * abs_x;

            // 4. 상대 속도 (웨이퍼 점 - 패드 점)
            const v_rel_x = v_global_x - v_pad_x;
            const v_rel_y = v_global_y - v_pad_y;
            const speed_rel = Math.sqrt(v_rel_x * v_rel_x + v_rel_y * v_rel_y);

            // 5. 누적 거리 (개선된 사다리꼴 공식)
            if (k > 0) {
              const prev_speed = trajectoryData[i][k-1]?.speed || 0;
              cumulativeDistance[i] += (speed_rel + prev_speed) * dt / 2;
            }

            // 6. 패드 좌표계에서의 위치 (수정된 변환)
            const pad_angle = omega_pad * t;
            const cos_p = Math.cos(-pad_angle);  // 패드 좌표계로의 역변환
            const sin_p = Math.sin(-pad_angle);

            const pad_local_x = abs_x * cos_p - abs_y * sin_p;
            const pad_local_y = abs_x * sin_p + abs_y * cos_p;

            padTrajectoryData[i].push({
              time: t,
              x: pad_local_x,
              y: pad_local_y,
              abs_x: abs_x,
              abs_y: abs_y
            });

            // 하단 웨이퍼 궤적 (대칭)
            const lower_abs_x = wafer_xc + (wx * cos_w - wy * sin_w);
            const lower_abs_y = -wafer_yc - (wx * sin_w + wy * cos_w);  // Y축 대칭

            const lower_pad_local_x = lower_abs_x * cos_p - lower_abs_y * sin_p;
            const lower_pad_local_y = lower_abs_x * sin_p + lower_abs_y * cos_p;

            padTrajectoryDataLower[i].push({
              time: t,
              x: lower_pad_local_x,
              y: lower_pad_local_y,
              abs_x: lower_abs_x,
              abs_y: lower_abs_y
            });

            trajectoryData[i].push({
              time: t,
              x: abs_x,
              y: abs_y,
              local_x: wx,
              local_y: wy,
              speed: speed_rel,
              cumulative_distance: cumulativeDistance[i],
              zone: zone,
              v_rel_x: v_rel_x,
              v_rel_y: v_rel_y
            });

            timeStepData.points.push({
              id: i,
              x: abs_x,
              y: abs_y,
              local_x: wx,
              local_y: wy,
              speed: speed_rel,
              cumulative_distance: cumulativeDistance[i],
              zone: zone
            });
          }

          // 히트맵용 점들 계산 (동일한 수정된 물리 모델 적용)
          for (let i = 0; i < heatmap_wafer_points.length; i++) {
            const {x: wx, y: wy, zone} = heatmap_wafer_points[i];

            const abs_x = wafer_xc + (wx * cos_w - wy * sin_w);
            const abs_y = wafer_yc + (wx * sin_w + wy * cos_w);

            const v_rot_local_x = -omega_wafer * wy;
            const v_rot_local_y = omega_wafer * wx;

            const v_rot_global_x = v_rot_local_x * cos_w - v_rot_local_y * sin_w;
            const v_rot_global_y = v_rot_local_x * sin_w + v_rot_local_y * cos_w;

            const v_global_x = wafer_vxc + v_rot_global_x;
            const v_global_y = wafer_vyc + v_rot_global_y;

            const v_pad_x = -omega_pad * abs_y;
            const v_pad_y = omega_pad * abs_x;

            const v_rel_x = v_global_x - v_pad_x;
            const v_rel_y = v_global_y - v_pad_y;
            const speed_rel = Math.sqrt(v_rel_x * v_rel_x + v_rel_y * v_rel_y);

            if (k > 0) {
              const prev_speed = heatmap_trajectoryData[i][k-1]?.speed || 0;
              heatmap_cumulativeDistance[i] += (speed_rel + prev_speed) * dt / 2;
            }

            heatmap_trajectoryData[i].push({
              time: t,
              x: abs_x,
              y: abs_y,
              local_x: wx,
              local_y: wy,
              speed: speed_rel,
              cumulative_distance: heatmap_cumulativeDistance[i],
              zone: zone
            });
          }

          timeSeriesData.push(timeStepData);
        }

        // 통계 계산
        const finalDistances = cumulativeDistance;
        const maxDistance = Math.max(...finalDistances);
        const minDistance = Math.min(...finalDistances);
        const avgDistance = finalDistances.reduce((a, b) => a + b, 0) / finalDistances.length;
        const uniformityIndex = maxDistance > 0 ? 1 - (maxDistance - minDistance) / maxDistance : 1;

        setResults({
          wafer_points,
          heatmap_wafer_points,
          heatmap_trajectoryData,
          heatmap_cumulativeDistance,
          timeSeriesData,
          trajectoryData,
          padTrajectoryData,
          padTrajectoryDataLower,
          finalDistances,
          statistics: {
            maxDistance,
            minDistance,
            avgDistance,
            uniformityIndex
          },
          R_wafer,
          R_pad,
          time_steps,
          cell_area
        });

        setIsSimulating(false);
        console.log('시뮬레이션 완료 - 수정된 물리 모델 적용');
      }, 100);
    };
  }, [params, customPoints]);

  useEffect(() => {
    runSimulation();
  }, [runSimulation]);

  // 사용자 정의 점 함수들
  const addCustomPoint = () => {
    if (newPointRadius <= params.R_wafer && newPointRadius >= 0) {
      const newPoint = {
        id: Date.now(),
        radius: newPointRadius,
        angle: newPointAngle
      };
      setCustomPoints(prev => [...prev, newPoint]);
    }
  };

  const removeCustomPoint = (pointId) => {
    setCustomPoints(prev => prev.filter(p => p.id !== pointId));
  };

  // 애니메이션 제어
  const toggleAnimation = useCallback(() => {
    if (isPlaying) {
      isPlayingRef.current = false;
      setIsPlaying(false);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
        animationRef.current = null;
      }
    } else {
      if (!results || !results.timeSeriesData || results.timeSeriesData.length === 0) {
        return;
      }
      isPlayingRef.current = true;
      setIsPlaying(true);
    }
  }, [isPlaying, results]);

  useEffect(() => {
    if (!isPlaying || !results || !results.timeSeriesData || results.timeSeriesData.length === 0) {
      isPlayingRef.current = false;
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
        animationRef.current = null;
      }
      return;
    }

    if (isPlaying && isPlayingRef.current) {
      lastFrameTimeRef.current = 0;
      animationRef.current = requestAnimationFrame(animate);
    }

    return () => {
      isPlayingRef.current = false;
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
        animationRef.current = null;
      }
    };
  }, [isPlaying, animate]);

  const resetAnimation = useCallback(() => {
    isPlayingRef.current = false;
    setIsPlaying(false);
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
      animationRef.current = null;
    }
    lastFrameTimeRef.current = 0;
    setAnimationTime(0);
  }, []);

  useEffect(() => {
    return () => {
      isPlayingRef.current = false;
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
        animationRef.current = null;
      }
    };
  }, []);

  // 포인트 선택 핸들러
  const handlePointSelection = (pointIndex) => {
    if (isSelectingMode) {
      setUserSelectedPoints(prev => {
        if (prev.includes(pointIndex)) {
          return prev.filter(p => p !== pointIndex);
        } else {
          return [...prev, pointIndex].slice(-10);
        }
      });
    } else {
      setSelectedPoints(prev => {
        if (prev.includes(pointIndex)) {
          return prev.filter(p => p !== pointIndex);
        } else {
          return [...prev, pointIndex].slice(-5);
        }
      });
    }
  };

  // 매개변수 제어 패널
  const ControlPanel = () => (
    <div className="bg-white p-4 rounded-lg shadow-lg space-y-4">
      <h2 className="text-lg font-bold mb-3">시뮬레이션 매개변수 (수정된 물리 모델)</h2>

      <div className="grid grid-cols-8 gap-2 items-end">
        <div>
          <label className="block text-xs font-medium mb-1">웨이퍼R(mm)</label>
          <input
            type="number"
            value={params.R_wafer}
            onChange={(e) => setParams({...params, R_wafer: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">패드R(mm)</label>
          <input
            type="number"
            value={params.R_pad}
            onChange={(e) => setParams({...params, R_pad: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">패드RPM</label>
          <input
            type="number"
            value={params.rpm_pad}
            onChange={(e) => setParams({...params, rpm_pad: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">웨이퍼RPM</label>
          <input
            type="number"
            value={params.rpm_wafer}
            onChange={(e) => setParams({...params, rpm_wafer: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">시간(s)</label>
          <input
            type="number"
            value={params.time_total}
            onChange={(e) => setParams({...params, time_total: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">스텝수</label>
          <input
            type="number"
            value={params.num_time_steps}
            onChange={(e) => setParams({...params, num_time_steps: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div>
          <label className="block text-xs font-medium mb-1">진동폭(mm)</label>
          <input
            type="number"
            value={params.oscillation_amplitude}
            onChange={(e) => setParams({...params, oscillation_amplitude: Number(e.target.value)})}
            className="w-full px-2 py-1 text-sm border rounded"
          />
        </div>
        <div className="flex flex-col items-center">
          <label className="text-xs font-medium mb-1">진동</label>
          <input
            type="checkbox"
            checked={params.oscillation_enabled}
            onChange={(e) => setParams({...params, oscillation_enabled: e.target.checked})}
            className="w-4 h-4"
          />
        </div>
      </div>

      <div className="grid grid-cols-4 gap-2 items-end">
        <div>
          <label className="block text-xs font-medium mb-1">중심거리(mm)</label>
          <input
            type="number"
            value={params.R_c}
