      <div className="border-t pt-3">
        <h3 className="font-semibold text-sm mb-2">사용자 정의 점</h3>

        {/* 미리 정의된 점들 */}
        <div className="mb-3">
          <h4 className="text-xs font-medium mb-2">미리 정의된 점들:</h4>
          <div className="grid grid-cols-4 gap-2">
            {[
              { radius: 0, angle: 0, label: '중심 (0mm)' },
              { radius: 50, angle: 0, label: '50mm' },
              { radius: 100, angle: 0, label: '100mm' },
              { radius: 150, angle: 0, label: '150mm' }
            ].map((point, index) => {
              const isSelected = customPoints.some(p => p.radius === point.radius && p.angle === point.angle);
              return (
                <button
                  key={index}
                  onClick={() => {
                    if (isSelected) {
                      setCustomPoints(prev => prev.filter(p => !(p.radius === point.radius && p.angle === point.angle)));
                    } else {
                      const newPoint = {
                        id: `predefined_${point.radius}_${point.angle}`,
                        radius: point.radius,
                        angle: point.angle,
                        label: point.label
                      };
                      setCustomPoints(prev => [...prev, newPoint]);
                    }
                  }}
                  className={`px-2 py-1 text-xs rounded border ${
                    isSelected 
                      ? 'bg-blue-500 text-white border-blue-500' 
                      : 'bg-gray-100 text-gray-700 border-gray-300 hover:bg-gray-200'
                  }`}
                >
                  {point.label}
                </button>
              );
            })}
          </div>
        </div>

        {/* 사용자 정의 점 추가 */}
        <div className="flex items-center space-x-2 mb-2">
          <div>
            <label className="block text-xs font-medium mb-1">반지름(mm)</label>
            <input
